void run()
{
    ProjTable           projTable;
    str                 oneTimeCustomer;
    MCRCustSearchType   mcrCustSearchTypeLocal;

    salesTable_ds.create();
    element.modifyForm();
    if (enableDirectedSelling)
    {
        oneTimeCustomer = element.args().parm();
        if (oneTimeCustomer == 'OneTime')
        {
            salesTable.OneTimeCustomer = NoYes::Yes;
            salesTable_ds.refresh();
            shippingAndDelivery.visible(false);
            administration.visible(false);
        }
    }

    super();

    // Check if the parameter is set to load the last saved
    if (MCRFullTextParameters::find().SaveSearchType)
    {
        // Get the user's saved search type, if it's not default, set it.
        mcrCustSearchTypeLocal = MCRCustSearch::getCustSearchType();
        if (mcrCustSearchTypeLocal)
        {
            mcrCustSearchType.selection(enum2int(mcrCustSearchTypeLocal));
        }
    }

    if (salesTableForm.replacementOrder()
        || oneTimeCustomer)
    {
        salesTable.transferCustAccount(salesTableForm.replacementOrder());
        salesTable.InvoiceAccount = salesTableForm.invoiceAccount();
        salesTable_InvoiceAccount.setFocus();

        // Use the dlv mode from the original order if one still has not been
        // defaulted (the call to initFromCustTable() above will trigger
        // the call to findDefaultDlvMode()).
        if (salesTable.DlvMode)
        {
            salesTable.DlvMode = salesTableForm.parmMCROriginalDlvMode();
        }
    }

    if (salesTableForm.project())
    {
        projTable = ProjTable::find(salesTable.ProjId);
        if (projTable.ProjInvoiceProjId)
        {
            if (projTable.isAllowSalesOrdersForMultipleFundingSourcesParamEnabled())
            {
                // Sales orders for multiple funding sources feature enabled
                if  (!ProjInvoiceTable::hasExternalFundingSources(projTable.ProjInvoiceProjId))
                {
                    error("@Proj:NoExternalFundingSource");
                    element.close();
                }
            }
            else
            {
                // Sales orders for multiple funding sources feature disabled
                if (!projTable.isSalesOrderAllowedForMultipleFundingSources())
                {
                    error("@Proj:AllowSalesOrdersForMultipleFundingSourcesError");
                    element.close();
                }
            }
        }
    }

    if (!salesTableType)
    {
        element.close();
        throw Exception::Error;
    }

    // Added these lines to ensure these parts are disabled/removed
    // properly and may still be enabled later.
    element.mcrAllowCustomerEdit(false);

    // Delete the option to search by "Loyalty Card" from the MCRCustSearchType combo box
    // if the user is not in the Call Center channel when the form first runs.
    FormComboBoxControl formComboBoxControl = this.control(this.controlId('MCRCustSearchType'));
    McrCustSearch::deleteListItemFromComboBox(formComboBoxControl, enum2Str(MCRCustSearchType::LoyaltyCardNumber), !mcrCallCenterEnabled);

    if (SalesCreateOrderFocusOnFieldFlight::instance().isEnabled())
    {
        CustAccount accountNum = element.args().parm();
        if (accountNum == '')
        { 
            SalesTable_CustAccount.setFocus();
        }
    }
}

void setFieldAccess()
{
    boolean enableCurrency = true;

    if (salesTable.ProjId)
    {
        if (ProjInvoiceTable::isSalesCurrencyLocked(ProjTable::find(salesTable.ProjId).ProjInvoiceProjId))
        {
            enableCurrency = false;
        }
    }

    salesTable_ds.object(fieldNum(SalesTable, CurrencyCode)).allowEdit(enableCurrency);
    salesTable_ds.object(fieldNum(SalesTable, CurrencyCode)).skip(!enableCurrency);

    salesTable_ds.object(fieldNum(SalesTable, CustAccount)).allowEdit(salesTable.ProjId=='');
    salesTable_ds.object(fieldNum(SalesTable, CustAccount)).skip(salesTable.ProjId!='');

    salesTable_ds.object(fieldNum(SalesTable, FundingSource)).visible(salesTable.ProjId != '' 
        && ProjInvoiceTable::hasMultipleExternalFundingSources(ProjTable::find(SalesTable.ProjId).ProjInvoiceProjId));
    salesTable_ds.object(fieldNum(SalesTable, FundingSource)).allowEdit(salesTable.ProjId != '');

    salesTable_ds.object(fieldNum(SalesTable, InvoiceAccount)).allowEdit(salesTable.ProjId=='');
    salesTable_ds.object(fieldNum(SalesTable, InvoiceAccount)).skip(salesTable.ProjId!='');

    salesTable_ds.object(fieldNum(SalesTable, LanguageId)).allowEdit(salesTable.ProjId=='');
    salesTable_ds.object(fieldNum(SalesTable, LanguageId)).skip(salesTable.ProjId!='');
}

