/// <summary>
/// Init with tax payment field visible and editable.
/// Set the value of the tax with payment field.
/// </summary>
private void initWithTaxPayment_IN()
{
    TaxGSTEPZCodeHelper taxGSTEPZCodeHelper = TaxGSTEPZCodeHelper::construct(salesTable);

    FormDataObject withIGSTPayment;
    withIGSTPayment = salesTable_W_ds.object(fieldNum(SalesTable_W, WithIGSTPayment_IN));

    withIGSTPayment.visible(taxGSTEPZCodeHelper.isEPZCodeNotNone() || taxGSTEPZCodeHelper.isExportOrder());
    withIGSTPayment.allowEdit(taxGSTEPZCodeHelper.isSEZ() || taxGSTEPZCodeHelper.isExportOrder());
    salesTable_W.WithIGSTPayment_IN = taxGSTEPZCodeHelper.isEPZCodeNotNone();
}

