    }
}

void write()
{
    MCRSourceCode           sourceCode;
    SalesTable              salesTableLocal;
    SalesAgreementHeader	salesAgreementHeader;

    // Remove check to return if saleTabled exists
    // need to be able to call salesCreateOrder_writeserver even if the record
    // exists - if this does not occur defaulting logic is skipped
    if (!element.closedOk())
    {
        return;
    }
    try
    {
        ttsbegin;

        salesTableType.formMethodDataSourceWrite(element, salesTable_ds);

        SalesTableForm::salesCreateOrder_WriteServer(
            salesTable,
            salesAgreementId,
            salesTableForm.
            replacementOrder(),
            copyRMALines.value(),
            false,
            0);

        if (mcrSalesTable.validateMCRSalesTableUsed())
        {
            mcrSalesTable.SysExtensionSerializerExtensionMap::insertAfterBaseTable(salesTable);
        }
        if (mcrSalesTableShipping.validateMCRSalesTableShippingUsed())
        {
            mcrSalesTableShipping.SysExtensionSerializerExtensionMap::insertAfterBaseTable(salesTable);
        }
        if (isSalesTableWEnabled)
        {
            SalesTable_W.SysExtensionSerializerExtensionMap::insertAfterBaseTable(salesTable);
        }

        if (countryRegion_RU && salesTable_RU.SalesTable == 0)
        {
            // update the FK because the first child write is a no-op because the parent record ID is not known
            salesTable_RU.SalesTable = salesTable.RecId;
            salesTable_RU_ds.write();
        }

        // <GBR>
        if (BrazilParameters::isEnabled())
        {
            salesTable_BR.SysExtensionSerializerExtensionMap::insertAfterBaseTable(salesTable);
        }
        // </GBR>
        // Apply all defaults and create free gift lines from the source offer.  Must happen
        // after insert, because we are adding a line.
        if (mcrSalesTable.SourceId)
        {
            salesTableLocal     = SalesTable::find(salesTable.SalesId, true);
            sourceCode          = MCRSourceCode::construct(salesTable);
            salesTableLocal     = sourceCode.mcrInitFromSourceCodeSetup(MCRSourceCodeSetup::find(mcrSalesTable.SourceId),
                                                                        salesTable.CustAccount);
            if (salesTableLocal.validateWrite())
            {
                salesTableLocal.update();
            }
            else
            {
                throw error("@SYS104318");
            }
        }

        if (salesTableForm.replacementOrder()
            && salesTableForm.returnItemNum())
        {
            salesAgreementHeader = salesTableForm.getSalesAgreementHeaderForReturnOrder();

            if (salesAgreementHeader)
            {
                TMSSalesTable::initFromTMSSalesAgreement(salesTableLocal.SalesId, salesAgreementHeader.RecId);
            }
        }

        ttscommit;
        // Prompt to copy from item lists.
        if (salesTable.SalesType == SalesType::Sales
            && !salesTableForm.replacementOrder()
            && SalesParameters::find().mcrPromptForItemList
            && MCRCustTable::hasAnyItemListGenerated(salesTable.CustAccount))
        {
            element.mcrCreateLinesFromItemList();
        }
    }

    catch (Exception::Error)
    {
        salesTable.RecId = 0;
        element.close();
        throw Exception::Error;
    }

    salesTable_ds.reread();
    salesTable_ds.refresh();

    salesTableForm.salesTable(salesTable);

    cancel.enabled(false);
}

/// <summary>
/// Determines whether the current record is valid and ready to be written to the database.
/// </summary>
/// <returns>
/// true if the record is valid; otherwise, false.
/// </returns>
boolean  validateWrite()
{
    boolean                     ret;
    NoYes                       noYes;

    #ISOCountryRegionCodes

    ret = super();

    if (ret
        && SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN])
        && TaxParameters::find().Customs_IN)
    {
        if (salesTable.CustomsExportOrder_IN    == NoYes::Yes &&
            salesTable.SalesType                != SalesType::Sales)
        {
            return checkFailed("@GLS5760");
        }
    }

    noYes = CustParameters::find().ReturnOrdersReasonReq;

    if (ret && !salesTable.ReturnReasonCodeId && noYes == NoYes::Yes && salesTable.ReturnStatus == ReturnStatusHeader::Created)
    {
        salesTable_ds.object(fieldNum(SalesTable, ReturnReasonCodeId)).mandatory(true);
        ret = checkFailed(strFmt("@SYS116347", fieldPName(SalesTable,ReturnReasonCodeId)));
    }

    if (ret && !salesTable.ReturnItemNum && salesTable.ReturnStatus == ReturnStatusHeader::Created)
    {
        // RMA number must be specified
        ret = checkFailed("@SYS110328");
    }

    if (ret)
    {
        if (!salesTableType.formMethodDataSourceValidateWrite(element, salesTable_ds)
            || (element.closedOk() &&
            !SalesCalcAvailableDlvDates::validateWrite(salesTable)
            ))

        {
            ret = false;
        }

        if (salesAgreementId)
        {
            SalesAgreementHeader salesAgreementHeader = SalesAgreementHeader::findAgreementId(salesAgreementId);
            if (!element.canSalesAgreementBeLinked(salesAgreementHeader, salesAgreementId))
            {
                element.removeSalesAgreement();
                ret = false;
            }
        }
    }

    ret = ret && mcrSalesTable_ds.validateWrite();

    return ret;
}

