    if (custTableLocal.mcrMergedRoot)
    {
        checkFailed(strFmt("@MCR12337",salesTable.CustAccount,custTableLocal.mcrMergedRoot));
        // Return the parent account number
        salesTable.CustAccount = custTableLocal.mcrMergedRoot;
    }

    editSalesAgreementId.allowEdit(salesTable.CustAccount ? true : false);

    return ret;
}

