public boolean validate()
{
    ProjTable   projTableLocal;
    boolean ret;

    ret = super();

    projTableLocal = ProjTable::find(salesTable.ProjId);
    if (projTableLocal)
    {
        if (!projTableLocal.type().ctrlSalesOrderTable())
        {
            ret = checkFailed(strFmt("@SYS53011", projTableLocal.Type));
        }
        if (ret)
        {
            if (!projTableLocal.isAllowSalesOrdersForMultipleFundingSourcesParamEnabled())
            {
                // Sales orders for multiple funding sources feature disabled
                if (!projTableLocal.isSalesOrderAllowedForMultipleFundingSources())
                {
                    ret = checkFailed("@Proj:AllowSalesOrdersForMultipleFundingSourcesError");
                }
            }
        }
    }

    return ret;
}

