    QueryBuildDataSource    qbdLogisticsPostalAddress;
    QueryBuildDataSource    qbdLogisticsLocation;

    QueryBuildRange         qbrIsPrimary;
    QueryBuildRange         qbrIsPostalAddress;

    super();

    if (enableDirectedSelling)
    {
        qbdLogisticsPostalAddress = this.queryBuildDataSource();
        qbdLogisticsLocation = qbdLogisticsPostalAddress.addDataSource(tableNum(LogisticsLocation));
        qbdLogisticsLocation.relations(true);
        qbdDirPartyLocation = qbdLogisticsLocation.addDataSource(tableNum(DirPartyLocation));
        qbdDirPartyLocation.relations(true);

        qbrIsPrimary        = qbdDirPartyLocation.addRange(fieldNum(DirPartyLocation, IsPrimary));
        qbrIsPostalAddress  = qbdDirPartyLocation.addRange(fieldNum(DirPartyLocation, IsPostalAddress));

        qbrIsPrimary.value(SysQuery::value(NoYes::Yes));
        qbrIsPostalAddress.value(SysQuery::value(NoYes::Yes));
    }
}

