        // no-op because a record with an empty FK is not valid.
        super();
    }
}

/// <summary>
/// Set up source code in replacement order from original sales order.
/// </summary>
public void initValue()
{
    if(salesTableForm.replacementOrder() && salesTableForm.returnItemNum())
    {
        SalesTable salesTableReturnOrder;
        MCRReturnSalesTable mcrReturnSalesTable;

        select OriginalSalesId from mcrReturnSalesTable
            join salesTableReturnOrder
            where mcrReturnSalesTable.SalesTable == salesTableReturnOrder.recid
            && salesTableReturnOrder.ReturnItemNum == salesTableForm.returnItemNum()
            && salesTableReturnOrder.SalesType == SalesType::ReturnItem;

        SalesTable salesTableOfOriginalSO = SalesTable::find(mCRReturnSalesTable.OriginalSalesId);

        if(salesTableOfOriginalSO.mcrIsCallCenter())
        {
            MCRSalesTable mcrSalesTableOfOriginalSO = salesTableOfOriginalSO.mcrSalesTable(true);
            MCROrderParameters mcrOrderParameters = MCROrderParameters::find();

            Catalog catalog = Catalog::find(MCRSourceCodeSetup::find(mcrSalesTableOfOriginalSO.SourceId).Catalog);

            if (!mcrOrderParameters.mcrAllowExpiredSourceCode &&
                (catalog.ValidFrom >= DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()) ||
                 catalog.ValidTo <= DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone())))
            {
                //If the original source code is not valid then leave the SourceId field blank.
                MCRSalesTable.SourceId = '';
            }
            else
            {
                if (mcrOrderParameters.mcrExchangeSourceCode == MCRExchangeSourceCode::Original)
                {
                    MCRSalesTable.SourceId = mcrSalesTableOfOriginalSO.SourceId;
                }
                else if (mcrOrderParameters.mcrExchangeSourceCode == MCRExchangeSourceCode::DefaultSourceCode)
                {
                    MCRSalesTable.SourceId = mcrOrderParameters.mcrDefaultExchangeSourceID;
                }
            }
        }
    }

    super();
}

public void lookup(FormControl _formControl, str _filterStr)
{
    FormRun formRun;
    Args args;

    args = new Args();
    args.name(formStr(MCRSourceIdTargetLookup));
    // Use SysQuery::value since this will be used in a range
    args.parm(SysQuery::value(salesTable.custTable_CustAccount().Party));
    args.caller(_formControl);
    formRun = ClassFactory::formRunClassOnClient(args);
    formRun.init();
    this.performFormLookup(formRun, _formControl);
}

