        SalesTable_W.MerchantID_IN             = TaxInformationCustTable_IN::findByCustTable(SalesTable_W.ECommerceOperator_IN).MerchantID;
        SalesTable_W.ECommerceOperatorGSTIN_IN = TaxInformation_IN::findDefaultbyLocation(DirPartyLocation::findPrimaryPartyLocation(CustTable::find(SalesTable_W.ECommerceOperator_IN).Party).Location).GSTIN;
