        SalesTable_W.ECommerceOperator_IN      = '';
        SalesTable_W.MerchantID_IN             = '';
        SalesTable_W.ECommerceOperatorGSTIN_IN = 0;
    }
}

/// <summary>
/// Modify the field ASOHOrderClass on datasource sales table.
/// </summary>
/// <returns>True if the field is modified, false otherwise.</returns>
public boolean modified()
{
    boolean ret;

    ret = super();

    ASOHOrderClassTable orderClass = ASOHOrderClassTable::find(this.text());

    salesTable.SalesType        = orderClass.DefaultSalesType;
    salesTable.InventSiteId     = orderClass.InventSiteId;
    salesTable.InventLocationId = orderClass.InventLocationId;

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if (RetailEnableB2b2bAndEnhanceB2bOrdersFeature::isEnabled())
    {
        boolean enableRetailChannelEdit = salesTableForm.enableRetailChannelEdit(SalesTable_CustAccount.valueStr(), false);
        salesTable_RetailChannelTable.visible(enableRetailChannelEdit);
        salesTable_RetailChannelTable.allowEdit(enableRetailChannelEdit);

        // Reset channel field if customer is modified.
        SalesTable.RetailChannelTable = 0;
    }
    return ret;
}

