public Common lookupReference()
{
    if (RetailEnableB2b2bAndEnhanceB2bOrdersFeature::isEnabled()
        && salesTableForm.enableRetailChannelEdit(SalesTable_CustAccount.valueStr(), false))
    {
        return RetailChannelTable::onlineB2bChannelLookup(this, SalesTable_CustAccount.valueStr());
    }

    return new SysReferenceTableLookup().performFormLookup();
}

public boolean modified()
{
    boolean ret;
    ret = super();

    RetailChannelTable channel = RetailChannelTable::findByRecId(SalesTable_RetailChannelTable.value());
    SalesTable.InventLocationId  = channel.inventLocation;
    SalesTable.InventSiteId = InventLocation::find(SalesTable.InventLocationId).InventSiteId;
    SalesTable.CurrencyCode = channel.Currency;

    // If Channel is specified when Source exists, then Catalog linked to channel is considered not the catalog linked to Source
    if (RetailInitializeCatalogInRetailSalesLineDataSourceFlight::instance().isEnabled()
        && MCRSalesTable.SourceId && B2B2BDataHelper::shouldRespectCatalog(SalesTable_RetailChannelTable.value()))
    {
        MCRSourceCodeSetup souceCodeSetup = MCRSourceCodeSetup::find(MCRSalesTable.SourceId);

        info(strFmt("@Retail:RetailCatalogOnSourceIdWillBeIgnoredInfo", Catalog::find(souceCodeSetup.Catalog).CatalogId));
    }

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if (this.value() == NoYes::Yes)
    {
        salesTable.ReceiptDateConfirmed  = salesTable.ReceiptDateRequested;
        salesTAble.ShippingDateConfirmed = salesTAble.ShippingDateRequested;
    }
    else
    {
        salesTable.ReceiptDateConfirmed  = dateNull();
        salesTAble.ShippingDateConfirmed = dateNull();
    }

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if (element.ConfirmDates.value() == NoYes::Yes)
    {
        salesTable.ReceiptDateConfirmed  = salesTable.ReceiptDateRequested;
        salesTable.ShippingDateConfirmed = salesTAble.ShippingDateRequested;
    }

    return ret;
}

/// <summary>
/// Filters delivery mode as per the channel selected while creating the order
/// </summary>
public void lookup()
{
    if (RetailEnableB2b2bAndEnhanceB2bOrdersFeature::isEnabled() && SalesTable.RetailChannelTable)
    {
        RetailDlvModeChannelExploded::channelDeliveryModesLookUp(this, SalesTable.RetailChannelTable);
    }
    else
    {
        super();
    }
}

void clicked()
{
    if (salesTable_ds.validateWrite())
    {
        // Can't update if it hasn't been created yet
        if (salesTable.RecId)
        {
            salesTable.update();//super only calls .doUpdate, we need the update logic including .orig logic
        }
        super();
    }
}
