    salesCreateQuotationForm = SalesCreateQuotationForm::construct(_quotationType);

    salesQuotationTableType = salesQuotationTable.type();
    salesQuotationTableType.parmNumberSeqFormHandlerQuotationId(numberSeqFormHandlerQuotationId);
    salesQuotationTableType.formMethodQuotationTypeModifyPostChange(element,salesQuotationTable_ds);
}

