void init()
{
    smmOpportunityTable smmOpportunityTable;
    smmBusRelTable      smmBusRelTable;
    CustTable           custTable;

    super();

    if (!element.args().caller())
    {
        // Window must be called with parameters.
        throw error("@SYS22539");
    }

    salesQuotationTableForm = element.args().caller();

    switch (salesQuotationTableForm.parmSource().TableId)
    {
        case tableNum(smmOpportunityTable):
            smmOpportunityTable = salesQuotationTableForm.parmSource();

            accountType.allowEdit(false);

            if (DirPartyTableHelper::isCustomer(smmOpportunityTable.Party))
            {
                accountType.selectionAsSmmQuotationAccountType(smmQuotationAccountType::CustAccount);
                custTable = CustTable::findByPartyRecId(smmOpportunityTable.Party);
            }
            else
            {
                accountType.selectionAsSmmQuotationAccountType(smmQuotationAccountType::BusRelAccount);
                smmBusRelTable = smmBusRelTable::findByParty(smmOpportunityTable.Party);
            }

            // Hide template when called from opportunity
            tabTemplateGroup.visible(false);

            // Disable opportunity Id, customer and BR group when called from opportunity
            groupBussinessRelation.allowEdit(false);
            groupCustomerAccount.allowEdit(false);

            salesQuotationTable_OpportunityId.allowEdit(false);

            salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, QuotationType)).allowEdit(!salesQuotationTableForm.salesQuotationTable());
            break;

        case tableNum(smmBusRelTable):
            smmBusRelTable = salesQuotationTableForm.parmSource() as smmBusRelTable;
            if (smmBusRelTable.BusRelTypeId == smmBusRelTypeGroup::getCustomerType())
            {
                custTable = CustTable::findByPartyRecId(smmBusRelTable.Party);
            }
            break;

        case tableNum(CustTable):
            custTable = salesQuotationTableForm.parmSource() as CustTable;
            break;

        default:
            SalesCreateQuotationInitializationResult initResult = new SalesCreateQuotationInitializationResult();
            this.initializeByTableIdWithResult(salesQuotationTableForm.parmSource().TableId, initResult);
            if (initResult.parmHasResult())
            {
                custTable = initResult.parmCustTableResult();
                smmBusRelTable = initResult.parmSmmBusRelTableResult();
            }
            break;
    }

    if (custTable || smmBusRelTable)
    {
        accountType.allowEdit(false);
        if (custTable)
        {
            accountType.selection(DirPartyTableHelper::isCustomer(custTable.Party) ?smmQuotationAccountType::CustAccount : smmQuotationAccountType::BusRelAccount);
        }
        else if (smmBusRelTable)
        {
            accountType.selection(DirPartyTableHelper::isRelation(smmBusRelTable.Party) ?smmQuotationAccountType::BusRelAccount : smmQuotationAccountType::CustAccount);
        }
        salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, CustAccount)).allowEdit(false);
        salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, BusRelAccount)).allowEdit(false);
    }

    if (element.args().parmEnumType() == enumNum(QuotationType))
    {
        createQuotationType = element.args().parmEnum();
    }
    else
    {
        createQuotationType = salesQuotationTableForm.quotationType();
    }

    salesCreateQuotationForm = SalesCreateQuotationForm::construct(createQuotationType);

    if (createQuotationType == QuotationType::Project)
    {
        // Show the project ID field when creating a quotation of type project.
        salesQuotationTable_ProjIdRef.visible(true);

        if (custTable)
        {
            accountType.selectionAsSmmQuotationAccountType(DirPartyTableHelper::isCustomer(custTable.Party) ? smmQuotationAccountType::CustAccount : smmQuotationAccountType::BusRelAccount);
        }
        else if (smmBusRelTable)
        {
            accountType.selectionAsSmmQuotationAccountType(DirPartyTableHelper::isRelation(smmBusRelTable.Party) ? smmQuotationAccountType::BusRelAccount : smmQuotationAccountType::CustAccount);
        }
        else
        {
            // Handle the cases were project quoation is not called from Project or Contract
            accountType.selectionAsSmmQuotationAccountType(smmQuotationAccountType::CustAccount);
        }
    }

    if (!smmLicense::crm())
    {
        accountType.selectionAsSmmQuotationAccountType(smmQuotationAccountType::CustAccount);
    }

    prospect          = (accountType.selectionAsSmmQuotationAccountType() == smmQuotationAccountType::BusRelAccount) ? true : false;

    groupBussinessRelation.visible(prospect);
    groupCustomerAccount.visible(!prospect);
    logisticsPostalAddressNew.visible(!prospect);
    busRelLogisticsPostalAddressNew.visible(prospect);

    bankLGFeatureChecker = BankLGFeatureChecker::construct();
    salesQuotationTable_DS.object(fieldNum(SalesQuotationTable, BankDocumentType)).visible(bankLGFeatureChecker.checkFeatureEnabled());
    logisticsPostalAddress_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, CountryRegionId));

    // Depending if source code is required set the field to mandatory
    mcrSalesQuotationTable_ds.object(fieldNum(MCRSalesQuotationTable, SourceId))
        .mandatory(MCROrderParameters::find().mcrSourceCodeRequired);

    //<GTE>
    SalesQuotationTable_TaxGroup.visible(!TaxSolutionScopeIntegrationUtil::isCompanyEnabled());
    //</GTE>
}

