boolean mcrModifyCustomerAddress()
{
    Args            args;
    Formrun         formrun;
    MCRCustTableConfigure custTableConfigure = new MCRCustTableConfigure();
    boolean         ret = false;

    if (isConfigurationkeyEnabled(configurationKeyNum(MCRCallCenter)))
    {
        args = new Args();

        custTableConfigure.parmModifyCustAccount(salesQuotationTable.CustAccount);

        args.parmObject(custTableConfigure);

        // Modify the customer address and safe the new address to the quotation
        formrun = new MenuFunction(menuitemDisplayStr(CustTable),MenuItemType::Display).create(args);
        if (formrun)
        {
            formrun.run();
            formrun.wait();
        }
    }
    return ret;
}

    /// <summary>
    ///  Calls the customer search form, and loads the customer that is returned.
    /// </summary>
    void mcrRunCustomerSearch()
    {
        AccountNum      custSearchAccount;
        utcdatetime timeNow = DateTimeUtil::utcNow();

        // if there is a search string
        if (isConfigurationkeyEnabled(configurationKeyNum(TradeCustomerSearch))
            && custTable_MCRSearchText.text() 
            && timeNow > DateTimeUtil::addSeconds(formCloseTime, 1))
        {
            custSearchAccount = MCRCustSearch::searchDisplayForm(custTable_MCRSearchText.text(), custSearchType.selectionAsMCRCustSearchType(), true, element);
            if (SalesCreateQuotationOpenCustSearchFlight::instance().isEnabled())
            {
                formCloseTime = DateTimeUtil::utcNow();
            }
			// if a customer was found
            if (custSearchAccount != "")
            {
                custSearchAccount = strRem(custSearchAccount, ",");
                select firstonly custTableDS where custTableDS.AccountNum == custSearchAccount;
                if (custTableDS)
                {
                    accountType.selectionAsSmmQuotationAccountType(smmQuotationAccountType::CustAccount);
                    accountType.modified();
                    salesQuotationTable.CustAccount = custSearchAccount;
                    salesQuotationTable_CustAccount.text(custSearchAccount);
                    salesQuotationTable_CustAccount.modified();
                }
                else
                {
                    // If the customer acount is not cleared, it will attempt to
                    //  find the invoice account related
                    //      Error occured when you search for a customer, and then
                    //          search for a BR (that doesn't have a customer)
                    accountType.selectionAsSmmQuotationAccountType(smmQuotationAccountType::BusRelAccount);
                    accountType.modified();
                    salesQuotationTable.CustAccount = "";
                    salesQuotationTable.BusRelAccount = custSearchAccount;
                    salesQuotationTable_BusRelAccount.text(custSearchAccount);
                    salesQuotationTable_BusRelAccount.modified();
                }

                element.mcrSetSourceCodeFocus();
            }
        }
    }

