void setFieldsActive()
{
    boolean showSales   = this.mustShowSales();
    boolean showProject = this.mustShowProject();

    // Set up fields according to selection
    groupCustomer.visible(showSales || showProject);
    groupReferences.visible(showSales || showProject);
    tabAdministration.visible(showSales || showProject);

    if (prospect)
    {
        salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, BusRelAccount)).mandatory(showSales || showProject);
        salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, CustAccount)).mandatory(false);
        salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, InvoiceAccount)).mandatory(false);
    }
    else
    {
        salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, BusRelAccount)).mandatory(false);
        salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, CustAccount)).mandatory(showSales || showProject);
        salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, InvoiceAccount)).mandatory(showSales || showProject);
    }

    // General group
    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, ReceiptDateRequested)).enabled(showSales || showProject);
    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, ShippingDateRequested)).enabled(showSales || showProject);
    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, QuotationExpiryDate)).enabled(showSales || showProject);
    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, CurrencyCode)).enabled(true);
    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, DlvMode)).enabled(showSales || showProject);
    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, InventLocationId)).enabled(showSales || showProject);
    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, DeliveryDateControlType)).enabled(showSales || showProject);

    // Template fields
    salesAvailableDlvDates.enabled(showSales || showProject);

    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable,InvoiceAccount)).allowEdit(!prospect);
    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable,ProjIdRef)).allowEdit(!prospect);
    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable,ProjInvoiceProjId)).allowEdit(!prospect);
}

