void write()
{
    MCRSourceCode mcrSourceCode;
    if (!element.closedOk())
    {
        return;
    }

    try
    {
        salesQuotationTableType.formMethodDataSourceWrite(element, salesQuotationTable_ds);
        salesQuotationTable.writeCreateQuotation();

        if (mcrSalesQuotationTable.SourceId || mcrSalesQuotationTable.DeliveryCompanyName)
        {
            mcrSalesQuotationTable.SalesQuotationTable = salesQuotationTable.RecId;
        }

        // Apply all defaults and create free gift lines from the source offer.  Must happen
        // after insert, because we are adding a line.
        if (mcrSalesQuotationTable.SourceId)
        {
            ttsbegin;

            salesQuotationTable_ds.reread();

            mcrSourceCode = MCRSourceCode::construct(salesQuotationTable);

            // dont need to assign the record again as it is changed in this call
            mcrSourceCode.mcrInitFromSourceCodeSetup(MCRSourceCodeSetup::find(mcrSalesQuotationTable.SourceId), salesQuotationTable.CustAccount);
            if (salesQuotationTable.validateWrite())
            {
                salesQuotationTable.update();
            }
            else
            {
                throw error("@SYS104318");
            }

            ttscommit;
        }
    }
    catch(Exception::Error)
    {
        salesQuotationTable.RecId = 0;
        element.close();
        throw Exception::Error;
    }

    salesQuotationTable_ds.reread();
    salesQuotationTable_ds.refresh();

    salesQuotationTableForm.salesQuotationTable(salesQuotationTable);

    cancel.enabled(false);
}

