public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    Common ret, entity;

    container inUseAddresses = LogisticsLocationSelectionLookup::transactionAssociatedAddresses(
        tableNum(SalesQuotationLine),
        fieldNum(SalesQuotationLine, DeliveryPostalAddress),
        fieldNum(SalesQuotationLine, QuotationId),
        salesQuotationTable.QuotationId,
        salesQuotationTable.DeliveryPostalAddress);

    if (salesQuotationTable.CustAccount)
    {
        entity = CustTable::find(salesQuotationTable.CustAccount);
    }
    else
    {
        entity = smmBusRelTable::find(salesQuotationTable.BusRelAccount);
    }

    ret = LogisticsLocationSelectionLookup::lookupAddressForm(_formReferenceControl,
        entity,
        [LogisticsLocationRoleType::Delivery],
        false,
        inUseAddresses,
        salesQuotationTable);

    return ret;
}

