/// <summary>
///     Validates that the user entered customer account is not a merged account.
/// </summary>
/// <returns>
///     true if the customer account entered is not a merged account; otherwise, false.
/// </returns>
/// <remarks>
///     Validation to not allow a user to manually enter a phantom custAccount.
///     This was added at the datasource level instead of table level since only want
///     to validate and restrict use of phantom accounts at order entry and front end forms.
/// </remarks>
public boolean validate()
{
    CustTable custTable = salesQuotationTable.custTable_InvoiceAccount();
    boolean ret = super();

    if (custTable.mcrMergedParent)
    {
        ret = false;
    }

    return ret;
}

public void lookup(FormControl _formControl, str _filterStr)
{
    SalesQuotationTable::lookupOpportunityId(_formControl, _filterStr, salesQuotationTable.party());
}

public boolean validate()
{
    boolean     ret = super();
    ProjTable   projTable;

    if (ret)
    {
        projTable = ProjTable::find(salesQuotationTable.ProjIdRef);
    }

    if (ret && projTable && salesQuotationTable && salesQuotationTable.validateProjectTypeByProject(projTable))
    {
        ret = checkFailed("@SYS98421");
    }

    if (ret && projTable.Status == ProjStatus::Completed)
    {
        ret = checkFailed("@SYS98422");
    }

    if (ret && projTable.CustAccount != salesQuotationTable.CustAccount && salesQuotationTable.CustAccount)
    {
        ret = checkFailed("@SYS98449");
    }

    return ret;
}

public void lookup(FormControl _formControl, str _filterStr)
{
    SysTableLookup sysTableLookup = element.addProjIdRefFieldsForLookup(_formControl);
    Query query = element.buildProjIdRefQueryForLookup();

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

