public boolean validate()
{
    boolean ret;

    //need end date to match start date to avoid trigger field validation
    if (SalesQuotationTable.PSAEstProjStartDate > SalesQuotationTable.PSAEstProjEndDate)
    {
        SalesQuotationTable.PSAEstProjEndDate = SalesQuotationTable.PSAEstProjStartDate;
    }

    ret = super();

    return ret;
}

public void modified()
{
    super();

    element.changeType(salesQuotationTable.QuotationType);
    salesQuotationTableForm.quotationType(salesQuotationTable.QuotationType);
}

public boolean validate()
{
    boolean ret = super();

    if (!ret)
    {
        return ret;
    }

    if (!salesQuotationTable.isTemplate())
    {
        // template must be active
        if (salesQuotationTable.TemplateGroupId && !SalesQuotationTemplateGroup::hasActiveTemplates(salesQuotationTable.TemplateGroupId, salesQuotationTable.QuotationType))
        {
            // Template Group does not have active templates.
            ret = checkFailed(strFmt("@SYS99925", salesQuotationTable.TemplateGroupId));
        }
    }

    return ret;
}

public boolean validate()
{
    boolean ret = super();

    if (ret)
    {
        if (! SalesQuotationTable::existTemplate(salesQuotationTable.QuotationType,salesQuotationTable.TemplateGroupId, salesQuotationTable.TemplateName))
        {
            // Template does not exist.
            ret = checkFailed("@SYS30623");
        }

        // template must be active
        if (ret && salesQuotationTable.TemplateName && SalesQuotationTable::findTemplate(salesQuotationTable.QuotationType,salesQuotationTable.TemplateGroupId, salesQuotationTable.TemplateName).TemplateActive == NoYes::No)
        {
            // Template is not active.
            ret = checkFailed("@SYS99743");
        }
    }

    return ret;
}

public void lookup(FormControl _formControl, str _filterStr)
{
    SalesQuotationTable::lookupTemplateName(_formControl, salesQuotationTable.QuotationType, salesQuotationTable.TemplateGroupId);
}

public Common resolveReference(FormReferenceControl _formReferenceControl)
{
    Common ret = super(_formReferenceControl);

    if (createQuotationType == QuotationType::Project && ret.RecId && !ProjWorkerLookup::validateWorker(ret.RecId, salesQuotationTable))
    {
        ret.clear();
    }
    else if (!ret.RecId || !HcmWorkerLookup::newOnlyActiveWorkers().validateWorker(ret.RecId))
    {
        ret.clear();
    }

    return ret;
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    if (createQuotationType == QuotationType::Project)
    {
        return ProjWorkerLookup::lookupWorker(_formReferenceControl, salesQuotationTable);
    }

    return HcmWorkerLookup::newOnlyActiveWorkers().lookupWorker(_formReferenceControl);
}

