public boolean leaveRecord(boolean _forceUpdate = false)
{
    boolean ret;

    if (mcrSalesQuotationTable.SalesQuotationTable)
    {
        ret = super(_forceUpdate);
    }
    else
    {
        ret = true;
    }

    return ret;
}

public boolean modified()
{
    boolean ret = super();
    boolean newProspectVal;

    if (ret)
    {
        newProspectVal = (this.selectionAsSmmQuotationAccountType() == smmQuotationAccountType::BusRelAccount) ? true : false;
    }

    if (ret && prospect != newProspectVal)
    {
        prospect = newProspectVal;
        if (prospect)
        {
            salesQuotationTable.BusRelAccount = '';
            salesQuotationTable_ds.object(fieldNum(SalesQuotationTable,BusRelAccount)).modified();
        }
        else
        {
            salesQuotationTable.CustAccount   = '';
            salesQuotationTable_ds.object(fieldNum(SalesQuotationTable,CustAccount)).modified();
        }

        element.setFieldsActive();

        groupBussinessRelation.visible(prospect);
        groupCustomerAccount.visible(!prospect);
        logisticsPostalAddressNew.visible(!prospect);
        busRelLogisticsPostalAddressNew.visible(prospect);
    }

    return ret;
}

public smmQuotationAccountType selectionAsSmmQuotationAccountType(smmQuotationAccountType _accountType = any2Enum(this.selection()))
{
    if (!prmIsDefault(_accountType))
    {
        this.selection(enum2int(_accountType));
    }
    return _accountType;
}

public MCRCustSearchType selectionAsMCRCustSearchType()
{
    return any2Enum(this.selection());
}

