public boolean modified()
{
    boolean         ret;

    ret = super();

    // Check if the input exists in ContactPerson.Name
    if (ContactPerson::find(editContactPersonName.text()).personName())
    {
        // Set the contactPersonName with the ContactPerson.Name
        contactPersonName = ContactPerson::find(editContactPersonName.text()).personName();
    }
    else
    {
        // Set the contactPersonName with the input text.
        contactPersonName = editContactPersonName.text();
    }

    if (!ContactPerson::findNameParty(salesQuotationTable.party(), contactPersonName))
    {
        salesQuotationTable.ContactPersonId = ContactPerson::findOrCreateNameParty(salesQuotationTable.party(), editContactPersonName.text()).ContactPersonId;
    }

    return ret;
}

