[Form]
public class SalesDeliverySchedule extends FormRun
{
    SalesTableForm_DeliverySchedule salesTableForm;
    SalesTable                      salesTable;
    boolean                         askingModifyInvoiced;
    boolean                         dataChanged;
    RecId                           tmpSourceDocumentLineRecId;
    // <GIN>
    boolean                         isSalesLine_INEnabled;
    #isoCountryRegionCodes
    // </GIN>
    boolean                         isSalesLine_intrastatEnabled;
    boolean                         isMCRSalesLineEnable;

}

void  close()
{
    xSysLastValue::saveLast(salesTableForm);

    super();
}

public void closeCancel()
{
    if (!dataChanged || Box::yesNo("@SYS107523", DialogButton::No) == DialogButton::Yes)
    {
        super();
    }
}

public void closeOk()
{
    FormDataSource extensionTableformDataSource;

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        extensionTableformDataSource = salesLine_BR_ds;
    }
    // </GBR>
    // <GIN>
    else if (isSalesLine_INEnabled)
    {
        extensionTableformDataSource = salesLine_IN_ds;
    }
    // </GIN>
    // <GEECZ>
    if (isSalesLine_intrastatEnabled)
    {
        extensionTableformDataSource = salesLine_Intrastat_ds;
    }
    // </GEECZ>

    if (PdsGlobal::pdsIsCWItem(salesLine.itemid))
    {
        if (!this.totalRemainingCWQty()
            || Box::yesNo("@PDS1436",DialogButton::Yes) == DialogButton::Yes)
        {
            if (salesTableForm.closeOk(salesLine_ds, extensionTableformDataSource))
            {
                super();
            }
        }
    }
    else
    {
        // Remaining quantity is not 0. Do you want to update the order line of the delivery schedule?
        if (!this.remainingQty()
            || Box::yesNo("@SYS131412",DialogButton::Yes) == DialogButton::Yes)
        {
            if (salesTableForm.closeOk(salesLine_ds, extensionTableformDataSource))
            {
                super();
            }
        }
    }
}

    public void  init()
    {
        if (! element.args().caller()
        ||  ! element.args().record())
        {
            throw error("@SYS22539");
        }

        // <GIN>
        isSalesLine_INEnabled = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]);
        // </GIN>
		// <GEECZ>
        isSalesLine_intrastatEnabled = SysExtensionSerializerExtensionMap::isExtensionEnabled(tableNum(SalesLine_Intrastat));
		// </GEECZ>

        tmpSourceDocumentLineRecId = (select maxOf(recId) from SourceDocumentLine).recId + 1;

        super();
    }

display SalesQty remainingQty()
{
    return salesTableForm.getOrderLineQty() - this.totalQty();
}

display PdsCWInventQty totalCWQty()
{
    PdsCWInventQty qty;

    SalesLine localSalesLine;

    localSalesLine.setTmp();
    localSalesLine.setTmpData(salesLine_ds.cursor());

    while select RecId, PdsCWQty from localSalesLine
    {
        qty += (localSalesLine.RecId == salesLine.RecId ? salesLine.PdsCWQty : localSalesLine.PdsCWQty);
    }

    return qty;
}

display SalesQty totalQty()
{
    SalesQty qty;

    SalesLine localSalesLine;

    localSalesLine.setTmp();
    localSalesLine.setTmpData(salesLine_ds.cursor());

    while select RecId, SalesQty from localSalesLine
    {
        qty += (localSalesLine.RecId == salesLine.RecId ? salesLine.SalesQty : localSalesLine.SalesQty);
    }

    return qty;
}

display PdsCWInventQty totalRemainingCWQty()
{
    return salesTableForm.pdsGetCWQty() - this.totalCWQty();
}

private SalesDeliveryScheduleFormContext createDeliveryScheduleFormContext(SalesLine _currentlyEditedSalesLine)
{
    SalesDeliveryScheduleFormContext formContextLocal = SalesDeliveryScheduleFormContext::constructCommon(element.args().record());

    SalesLine deliverySalesLineLocal;
    deliverySalesLineLocal.setTmp();
    deliverySalesLineLocal.setTmpData(salesLine_ds.cursor());

    // skip currently edited line. It is separetly passed to delivery date calculation logic to be validated.
    while select deliverySalesLineLocal where deliverySalesLineLocal.RecId != _currentlyEditedSalesLine.RecId
    {
        formContextLocal.addLine(deliverySalesLineLocal);
    }

    return formContextLocal;
}

public void linkActive()
{
    SalesLine salesLineLocal;

    if (element.args().record().TableId == tablenum(SalesLine))
    {
        salesLineLocal = element.args().record();
        salesTable = salesLineLocal.salesTable();

        salesTableForm = SalesTableForm::construct(SalesTableFormId::DeliverySchedule, element.args().record());

        // <GBR>
        if (BrazilParameters::isEnabled())
        {
            salesTableForm.salesLine_Init(salesLine, salesLine_BR);
        }
        // </GBR>
        // <GIN>
        else if (isSalesLine_INEnabled)
        {
            salesTableForm.salesLine_Init(salesLine, salesLine_IN);
        }
        // </GIN>
        // <GEECZ>
        else if (isSalesLine_IntrastatEnabled)
        {
            salesTableForm.salesLine_Init(salesLine, salesLine_Intrastat);
        }
        // </GEECZ>
        else if (salesTable.mcrIsCallCenter())
        {
            salesTableForm.salesLine_Init(salesLine, mcrSalesLine);
            isMCRSalesLineEnable = true;
        }
        else
        {
            salesTableForm.salesLine_Init(salesLine);
        }

        TabPageCharges.visible(salesLineLocal.LineDeliveryType == LineDeliveryType::OrderLine);
        dataChanged = false;
    }

    super();
}

public boolean validateDelete()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        if (salesLine.pickedInTotalSalesUnit())
        {
            // Updating has been interrupted because there is an inventory transaction with the status %1.
            ret = checkFailed(strfmt("@SYS19413", StatusIssue::Picked));
        }
    }
    return ret;
}

public boolean leaveRecord(boolean _forceUpdate = false)
{
    boolean ret;

    if (!salesLine.RecId)
    {
        _forceUpdate = true;
    }

    ret = super(_forceUpdate);

    return ret;
}

int  active()
{
    int         ret;

    ret = super();

    salesLine_ds.object(fieldnum(SalesLine, SalesDeliverNow)).allowEdit(salesLine.DeliveryType != TradeLineDlvType::DropShip);

    salesLine_ds.allowCreate(salesTableForm.createLineAllowed(salesLine));
    salesLine_ds.allowEdit  (salesTableForm.editLineAllowed());
    salesLine_ds.allowDelete(salesTableForm.deleteLineAllowed(salesLine));

    salesTableForm.interCompanySetLineAccess(salesLine);

    return ret;
}

void  initValue()
{
    salesTableForm.salesLine_InitValue(salesLine);

    super();
}

void  create(boolean  append = true)
{
    if (salesTable.checkUpdate())
        super(append);

    salesLine_ds.refresh();
}

void  write()
{
    NumberSeq   numberSeq;

    if (!askingModifyInvoiced)
    {
        salesTableForm.salesLine_Write(salesLine);

        if (this.validateWrite())
        {
            if (salesLine.RecId)
            {
                salesLine.doUpdate();
            }
            else
            {
                numberSeq = NumberSeq::newGetNum(InventParameters::numRefInventTransId());
                salesLine.InventTransId = numberSeq.num();

                if (!SalesDeliveryScheduleSourceDocumentLineFlight::instance().isEnabled())
                {
                    // setting to a dummy temporary sourceDocumentLine to avoid index violations on the temp table.
                    // SourceDocumentLine will be created and its RecId assigned in purchLine.insert()
                    salesLine.SourceDocumentLine = tmpSourceDocumentLineRecId;
                    tmpSourceDocumentLineRecId++;
                }

                SalesLine line;
                line.setTmp();
                line.setTmpData(SalesLine_ds.cursor());
                salesLine.LineCreationSequenceNumber = salesTableForm.nextCreationSequenceNumber(line, salesLine.SalesId);

                salesLine.doInsert();
                // <GIN>
                if (isSalesLine_INEnabled)
                {
                    salesLine_IN.SysExtensionSerializerExtensionMap::insertAfterBaseTable(salesLine);
                }
                // </GIN>
                // <GEECZ>
                if (isSalesLine_IntrastatEnabled)
                {
                    salesLine_Intrastat.SysExtensionSerializerExtensionMap::insertAfterBaseTable(salesLine);
                }
                // </GEECZ>
            }

            dataChanged = true;

            salesLine_ds.reread();
            salesLine_ds.refresh();
        }
    }
}

public void delete()
{
    // <GEECZ><GIN>
    SalesLineRefRecId salesLineRecId = salesLine.RecId;
    // </GIN></GEECZ>
    // <GIN>
    SalesLine_IN salesLine_INLocal;
    // </GIN>
    // <GEECZ>
    SalesLine_Intrastat salesLine_IntrastatLocal;
    // </GEECZ>

    if (salesLine.RecId)
    {
        if (salesLine.checkDelete())
        {
            super();
            dataChanged = true;

            // <GIN>
            if (isSalesLine_INEnabled)
            {
                salesLine_INLocal.SetTmp();
                salesLine_INLocal.SetTmpData(salesLine_IN);

                delete_from salesLine_INLocal where salesLine_INLocal.SalesLine == salesLineRecId;
            }
            // </GIN>
            // <GEECZ>
            if (isSalesLine_IntrastatEnabled)
            {
                salesLine_IntrastatLocal.SetTmp();
                salesLine_IntrastatLocal.SetTmpData(salesLine_Intrastat);

                delete_from salesLine_IntrastatLocal where salesLine_IntrastatLocal.SalesLine == salesLineRecId;
            }
            // </GEECZ>
        }
    }
    else
    {
        super();
    }
}

public boolean validateWrite()
{
    boolean                     ret;
    SalesLine                   salesLineOrig;
    SalesLine                   localSalesLine;
    SalesCalcAvailableDlvDates  salesCalcAvailableDlvDates;

    salesTableForm.setTouched(salesTable);

    if (salesLine.RecId != 0
    &&  salesLine.InterCompanyInventTransId
    &&  salesTable.InterCompanyOrder)
    {
        salesLine.DeliveryDateControlType = SalesDeliveryDateControlType::None;
    }

    ret = super();

    if (!askingModifyInvoiced && ret)
    {
        askingModifyInvoiced = true;
        ret = salesTable.checkUpdate();
        if (!ret)
        {
            salesLine_ds.reread();
        }
        askingModifyInvoiced = false;
    }

    if (ret)
    {
        ret = salesTableForm.salesLine_validateWrite(salesLine, salesLine.inventDim());
    }

    if (ret && salesLine.DeliveryDateControlType)
    {
        // workaround that prevents salesLine.dataareaid from being reset when invoked on a temporary table buffer.
        Global::buf2Buf(salesLine, localSalesLine);

        salesCalcAvailableDlvDates = SalesCalcAvailableDlvDates::newCommonSalesDlvDateType(localSalesLine);

        if (SalesATPCalculationDeliveryScheduleFormContextFeature::isEnabled())
        {
            using (SalesDeliveryScheduleFormContext context = element.createDeliveryScheduleFormContext(salesLine))
            {
                ret = salesCalcAvailableDlvDates.validateWritePrompt(localSalesLine,false,false,true,false);
            }
        }
        else
        {
            ret = salesCalcAvailableDlvDates.validateWritePrompt(localSalesLine,false,false,true,false);
        }

        Global::buf2Buf(localSalesLine, salesLine);
    }

    if (ret && salesLine.SalesQty < 0)
    {
        // Quantity cannot be negative on a delivery line.
        ret = checkFailed("@SYS131411");
    }

    if (ret && salesLine.RecId)
    {
        salesLineOrig = salesLine.orig();

        if (salesLineOrig && abs(salesLine.SalesQty)  <  abs(salesLineOrig.SalesQty) && salesLine.SalesQty * salesLineOrig.SalesQty >= 0 )
        {
            if (salesLine.RemainSalesPhysical * salesLineOrig.RemainSalesPhysical   < 0 ||
                salesLine.RemainSalesPhysical * salesLineOrig.RemainSalesFinancial  < 0)
            {
                // Quantity cannot be reduced because the remaining update quantity changes sign.
                ret = checkFailed("@SYS25508");
            }
        }
    }

    return ret;
}

edit DlvScheduleMarkupConversionMode parmMarkupConversionMode(boolean                         _set,
