/// <summary>
/// Refresh SalesParmLine recordset
/// </summary>
/// <remarks>
/// Refresh SalesParmLine record to ensure that any changes made in other
/// processes are reflected.
/// </remarks>
void doRefresh()
{
    SalesParmLine           pmfSalesParmLine;

    pmfSalesParmLine = salesParmLine.data();
    salesParmLine_ds.research();
    salesParmLine_ds.positionToRecord(pmfSalesParmLine);
}

/// <summary>
/// This method controls the accessibility and values of the Cash receipt fields.
/// </summary>
public void enabledDisabledPrintCODCashReceipt_W()
{
    if (printCODCashReceipt_W.visible())
    {
        boolean enableCashRegister = !salesFormLetter.proforma() && salesEditLinesForm.allowEditPrintCODCashReceipt_W();

        if (printCODCashReceipt_W.allowEdit(enableCashRegister))
        {
            printCODCashReceipt_W.value(salesFormLetter.printCODCashReceipt_W());
        }
        else
        {
            printCODCashReceipt_W.value(false);
        }

        boolean isCashRegisterSectionEmpty = true;
        if (SalesParmUpdate_DS.object(fieldNum(SalesParmUpdate, CashRegisterTerminal_W)).allowEdit(enableCashRegister))
        {
            if (SalesParmUpdate.CashRegisterTerminal_W == 0)
            {
                CashRegisterOperator_W cashRegisterOperator = CashRegisterOperator_W::currentOperator();

                if (cashRegisterOperator)
                {
                    CashRegisterTerminal_W cashRegisterTerminal = cashRegisterOperator.defaultTerminal();

                    isCashRegisterSectionEmpty = false;
                    LocationRecId.value(cashRegisterOperator.Location);
                    SalesParmUpdate.CashRegisterTerminal_W  = cashRegisterTerminal.RecId;
                    RegisterRecId.value(cashRegisterTerminal.CashRegister);
                    OperatorRecId.value(cashRegisterOperator.RecId);
                }
            }
        }

        if (isCashRegisterSectionEmpty)
        {
            LocationRecId.value(0);
            SalesParmUpdate.CashRegisterTerminal_W  = 0;
            RegisterRecId.value(0);
            OperatorRecId.value(0);
        }
    }
}

/// <summary>
///    This method controls the acessibility and values of the <c>PrintEntryCertificate_W</c> checkbox.
/// </summary>
public void enabledDisabledPrintEntryCertificate_W()
{
    SalesParmTable      salesParmTableLocal;

    if (printEntryCertificate_W.visible())
    {
        select firstonly RecId from salesParmTableLocal
            where salesParmTableLocal.ParmId == salesParmUpdate.ParmId
               && salesParmTableLocal.IssueOwnEntryCertificate_W;

        printEntryCertificate_W.allowEdit(updateNow.value() && salesParmTableLocal.RecId);
        printEntryCertificate_W.skip(! printEntryCertificate_W.allowEdit());

        if (! updateNow.value() || ! salesParmTableLocal.RecId || ! PrintEntryCertificate_W.allowEdit())
        {
            printEntryCertificate_W.value(NoYes::No);
        }
    }
}

public void enableEximFieldsActive_IN()
{
    EximParameters_IN eximParameters;
    boolean           isDEPBAllowedEdit;
    boolean           isDBKAllowedEdit;

    eximParameters    = EximParameters_IN::find();
    isDEPBAllowedEdit = eximParameters.ActivateDEPB && eximSalesParmLine_IN.ApplyDEPB;
    isDBKAllowedEdit  = eximParameters.ActivateDutyDrawback && eximSalesParmLine_IN && eximSalesParmLine_IN.dbkCenvatStatus != EximDBKCenvatStatus_IN::None;

    if (TaxParameters::find().EximIncentiveSchemes_IN
        && documentStatus == DocumentStatus::ShippingBill_IN)
    {
        tabLineIncentiveScheme.visible(true);
        salesParmTable_EximExportDateTime_IN.visible(true);
        depb.visible(eximParameters.ActivateDEPB);
        depb_MarketValue.allowEdit(isDEPBAllowedEdit);
        depb_MarketValuePct.allowEdit(isDEPBAllowedEdit);
        depb_AllowedMarketValue.allowEdit(isDEPBAllowedEdit);
        dutyDrawback.visible(eximParameters.ActivateDutyDrawback);
        dbkCustoms_CustomsTariffCodeTable.allowEdit(isDBKAllowedEdit);
        dutyDrawback_DBKRateType.allowEdit(isDBKAllowedEdit);
        dutyDrawback_DBKPercent.allowEdit(isDBKAllowedEdit);
    }
}

public void enableFiscalDocument_BR()
{
    boolean isNFE = FiscalDocModel_BR::find(salesParmTable.FiscalDocumentModel_BR).IsForNFeFederal;

    salesParmTable_DS.object(fieldNum(SalesParmTable, FiscalDocumentAccessKey_BR)).enabled(isNFE);
    salesParmTable_DS.object(fieldNum(SalesParmTable, FiscalDocumentSeries_BR)).enabled(!isNFE);
    salesParmTable_DS.object(fieldNum(SalesParmTable, FiscalDocumentNumber_BR)).enabled(!isNFE);
}

    public FormRun formRun()
    {
//Requiered by the SysOperation framework.
        return this;
    }

void  init()
{
    ParmId  parmId;
    SalesInstrumentationLogger instrumentationLogger;instrumentationLogger = SalesInstrumentationLogger::createLogger(formStr(SalesEditLines));
    using (var activity = instrumentationLogger.salesTableFormActivities().openSalesConfrimationForm())
    {
        boolean isTaxesMarked  = TaxParameters::checkTaxParameters_IN();
        boolean isCustomMarked = TaxParameters::find().Customs_IN;

        adjustedlineAmountCache = new Map(typeName2Type(extendedTypeStr(SalesParmLineRefRecId)), Types::Real);

        countryRegion_RU = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]);
        isEasternEuropeAllandRU = SysCountryRegionCode::isLegalEntityInCountryRegion(#easternEuropeAllandRU);
        countryRegion_IN = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]);
        countryRegion_BR = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoBR]);
        if (#PdsEnabled)
        {
            pdsFormCtrl = PdsFormCtrl_SalesEditLines::newFromForm(this);
            pdsFormCtrl.initPre();
        }

        super();

        hcmWorkerLookup = HcmWorkerLookup::newActiveWorkers();
        transportationPartyLookUp = TransportationPartyLookUp::construct();

        if (! element.args().caller())
        throw error("@SYS22539");

        taxIntegrationSystem_CN = CustParameters::isTaxIntegrationSystem_CN();

        salesFormLetter             = element.args().caller();
        documentStatus              = salesFormLetter.documentStatus();
        parmId                      = salesFormLetter.parmId();

        isOpenedFromPostingHistory  = salesFormLetter.parmCreateFromHistory();
        isHistoryReadOnly           = salesFormLetter.parmHistoryReadOnly();

        if (SalesFormLetterCreateFromBatchFlight::instance().isEnabled())
        {
            isOpenedFromBatch       = !salesFormLetter.parmOpenedWithArgs() 
                                        && salesFormLetter.parmCurrentBatch().RecId;
        }

        salesEditLinesForm          = SalesEditLinesForm::construct(documentStatus, salesFormLetter.createFromLines(), salesFormLetter.parmVersioningUpdateType());
        salesEditLinesForm.parmIsCorrectionOrCancel(salesFormLetter.isVersioningCorrectionOrCancel());

        if (countryRegion_IN)
        {
            salesParmLine_IN_AssessableValue_IN.visible(isTaxesMarked);
            price.visible(isTaxesMarked);
            salesParmUpdate_ds.object(fieldNum(SalesParmUpdate, CustomsExportOrderType_IN)).visible(isCustomMarked);

            if (isCustomMarked && documentStatus == DocumentStatus::ShippingBill_IN)
            {
                element.designShippingBillForm_IN();
                salesParmTable_ds.refresh();
            }
            if (documentStatus == DocumentStatus::Invoice && !salesFormLetter.proforma())
            {
                ButtonPrintExcise_IN.visible(true);
            }
        }
        if (countryRegion_IN && documentStatus == DocumentStatus::PackingSlip)
        {
            GateEntryDetails_IN.visible(true);
            InventSiteGateEntryLine.visible(true);
        }
        if (salesEditLinesForm.giro())
        {
            giro.visible(true);
        }
        else
        {
            giro.visible(false);
        }

        if (InventInventoryDataServiceSoftReservationHelper::isSoftReserveEnabled())
        {
            SalesParmLine_salesLineBlockLevel.visible(true);
            SalesParmLine_salesLineSoftReserveStatus.visible(true);
        }

        if (IntrastatCountryRegionParameters::isInEUByIsoCode())
        {
            transportationDocument.visible(documentStatus == DocumentStatus::Invoice);
        }

        salesParmUpdate_ReduceOnHand.visible(salesEditLinesForm.reduceOnHand());

        BillOfLading.visible(salesEditLinesForm.billOfLading());
        // <GBR>
        VehicleInformation_BR.visible(salesEditLinesForm.billOfLading());
        // </GBR>
        if (countryRegion_RU)
        {
            tabBillOfLading_RU.visible(salesEditLinesForm.billOfLading());
            printBillOfLading_RU.visible(tabBillOfLading_RU.visible());
            printTransportInvoice_RU.visible(printBillOfLading_RU.visible());
        }

        salesParameters = SalesParameters::find();
        Compiler.visible(salesParameters.TransportationDocumentOnPackingSlip);
        TransportationParty.visible(salesParameters.TransportationDocumentOnPackingSlip);
        TransportationPartyAdditionalInfo.visible(salesParameters.TransportationDocumentOnPackingSlip);

        isLoadPackingSlip           = documentStatus == DocumentStatus::PackingSlip && (salesFormLetter.parmWHSLoadId() !='') ;
        isTransportLoadPackingSlip  = documentStatus == DocumentStatus::PackingSlip && (salesFormLetter.parmIsPostingTransportLoads());

        salesParmUpdate_ds.object(fieldNum(SalesParmUpdate, SumSalesId)).visible(false);
        salesParmTable_ds.object(fieldNum(SalesParmTable, SalesId)).mandatory(true);
        salesParmLine_ds.object(fieldNum(SalesParmLine, OrigSalesId)).mandatory(true);
        salesParmSubTable_ds.object(fieldNum(SalesParmSubTable, OrigSalesId)).mandatory(true);
        salesParmUpdate_ds.query().dataSourceTable(tableNum(SalesParmUpdate)).addRange(fieldNum(SalesParmUpdate, ParmId)).value(queryValue(parmId));
        salesParmTable_ds.query().dataSourceTable(tableNum(SalesParmTable)).addRange(fieldNum(SalesParmTable, ParmId)).value(queryValue(parmId));

        salesEditLinesForm.setSalesParmUpdate(salesParmUpdate);

        if (salesFormLetter.lockSalesUpdateField())
        {
            salesParmUpdate_ds.object(fieldNum(SalesParmUpdate,SpecQty)).enabled(false);
        }
        else
        {
            specQty.items(salesEditLinesForm.specQtyItems());
        }


        if (CustPrepaymentInvoiceFeature::instance().isEnabled() && documentStatus == DocumentStatus::Invoice)
        {
            TabPrepaymentInvoices.visible(true);

            if (salesParmUpdate.SumBy != AccountOrder::None)
            {
                warning(strFmt("@AccountsReceivable:PrepaymentInvoiceSummaryUpdateWarning"));
            }
            else
            {
                QueryBuildDataSource qbdsCustPrepaymentCustInvoice;

                qbdsCustPrepaymentCustInvoice = custPrepaymentCustInvoice_ds.query().dataSourceTable(tableNum(CustPrepaymentInvoiceTable));
                qbdsCustPrepaymentCustInvoice = qbdsCustPrepaymentCustInvoice.addDataSource(tableNum(SalesParmSubTable));
                qbdsCustPrepaymentCustInvoice.joinMode(JoinMode::ExistsJoin);
                qbdsCustPrepaymentCustInvoice.addLink(fieldNum(SalesParmSubTable,OrigSalesId), fieldNum(CustPrepaymentInvoiceTable, SalesId));

                qbdsCustPrepaymentCustInvoice.addDynalink(fieldNum(SalesParmSubTable, ParmId), salesParmTable, fieldNum(SalesParmTable, ParmId));
            }
        }

        eInvoice_MX_CFDIEnabled_MX.visible(salesEditLinesForm.showCFDIEnabled_MX());
        eInvoiceCFDIReference_MX.visible(salesEditLinesForm.showCFDIReference_MX());

        if (pdsFormCtrl)
        {
            pdsFormCtrl.parmFormLetter(salesFormLetter);
            pdsFormCtrl.parmSalesEditLinesForm(salesEditLinesForm);
            pdsFormCtrl.setDatasources(
                        salesParmLine,
                        salesParmUpdate,
                        salesParmTable,
                        null,
                        null);
            pdsFormCtrl.initPost();
        }

        boolean canGoBatch = salesFormLetter.canGoBatch() && salesFormLetter.showBatchTab();
        this.batch(canGoBatch);

        if (salesFormLetter.batchInfo().parmBatchExecute())
        {
            salesFormLetter.batchInfo().parmBatchExecute(canGoBatch);
        }

        if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoCN]))
        {
            vatInfoCN.visible(taxIntegrationSystem_CN);
        }

        if (BankLCExportFeatureChecker::checkBankLCExportEnabled())
        {
            bankLCExportLineNumController =  BankLCExportLineNumController::construct();
            salesEditLinesForm.parmBankLCExportLineNumController(bankLCExportLineNumController);
        }
        salesParmTable_ds.object(fieldNum(SalesParmTable, BankLCExportLine)).visible(salesEditLinesForm.bankLCExportLine());

        element.updateDesign(InventDimFormDesignUpdate::Init);

        advanceInvoiceSettlement = salesFormLetter.parmAdvanceInvoiceSettlement_W();
        countryRegion_LTLV = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoLT, #isoLV]);

        if (countryRegion_LTLV)
        {
            docNumberingCode_LT.text(salesFormLetter.getSalesFormLetterContract().parmLtDocNumberingCode());
        }

        if (TaxThaiGovCertificationFeatureChecker::isUnrealizedVATEnabled())
        {
            this.updateDesignForUnrealizedVAT();
        }

        if (BrazilParameters::isEnabled())
        {
            element.setControlsVisibility_BR();
        }

        if (RetailSMB::IsRetailSMBEnabled())
        {
            RetailSMB::SetVisible(element);
        }

        if (TaxSolutionScopeIntegrationUtil::isCompanyEnabled())
        {
            TaxDocumentLauncher.visible(true);
            TaxTransSource.visible(false);
        }

        if (TaxIntegrationUtils::isMultipleTaxIdEnabledForBusinessProcess(TaxIntegrationBusinessProcess::Sales))
        {
            if (TaxHideTaxIDFromGUIWhenMutipleVATIDEnabledFlight::instance().isEnabled())
            {
                // When support multiple VAT registration numbers parameter is enabled, the tax id should be invisibile from UI.
                salesParmTable_ds.object(fieldNum(SalesParmTable, TaxId)).visible(false);
            }

            // When support multiple VAT registration numbers parameter is enabled, it should not edit tax id from the UI.
            salesParmTable_ds.object(fieldNum(SalesParmTable, TaxId)).allowEdit(false);
        }

        // <GIN>
        if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
        {
            salesParmTable_ProvisionalAssessment_IN.visible(documentStatus == DocumentStatus::Invoice);
            EWayBill_IN.visible(documentStatus == DocumentStatus::Invoice || documentStatus == DocumentStatus::ShippingBill_IN);
        }
        // </GIN>

        if (countryRegion_BR)
        {
            this.updateDesign_BR();
        }
    }
}

private void initOpenedFromBatch()
{
    if (isOpenedFromBatch)
    {
        SalesFormLetterContract formLetterContract = salesFormLetter.getSalesFormLetterContract();

        if (formLetterContract.parmChooseLinesQueryPacked())
        {
            QueryRun queryRun = new QueryRun(SysOperationHelper::base64Decode(formLetterContract.parmChooseLinesQueryPacked()));

            if (queryRun)
            {
                this.initQueryControl(queryRun.query());
            }
        }
    }
}

private boolean allowSaveChanges()
{
    boolean allowSave = true;

    if (this.isBatchReadOnly())
    {
        allowSave = false;
    }
    else if (this.isPostingHistoryReadOnly())
    {
        allowSave = false;

        element.design().controlName(formControlStr(SalesEditLines, OK)).enabled(false);
        ButtonBatch.enabled(false);
    }

    return allowSave;
}

private boolean isBatchReadOnly()
{
    boolean isReadOnly;

    if (isOpenedFromBatch            )
    {
        Batch localBatch = salesFormLetter.parmCurrentBatch();

        BatchJob localBatchJob;
        select Status from localBatchJob
            where localBatchJob.RecId == localBatch.BatchJobId;

        isReadOnly = localBatch.Status != BatchStatus::Hold || localBatchJob.Status != BatchStatus::Hold;
    }

    return isReadOnly;
}

private boolean isPostingHistoryReadOnly()
{
    return isOpenedFromPostingHistory
        && isHistoryReadOnly
        && SalesPostingHistoryUpdateStatusFlight::instance().isEnabled();
}

