public boolean validate()
{
    boolean ret = super();

    if (ret)
    {
        if (SalesParmUpdate.CashRegisterTerminal_W
            && CashRegisterTerminal_W::find(SalesParmUpdate.CashRegisterTerminal_W).Location != LocationRecId.value())
        {
            ret = checkFailed(strFmt("@FiscalServices:AS_SalesEditLineFormCashRegisterTerminal_WvalidateMethod", CashRegisterLocation_W::find(LocationRecId.value()).Code));
        }
    }

    return ret;
}

