    if (EInvoiceCFDIParameters_MX::isElectronicInvoiceEnabled())
    {
        this.updateCFDIReference_MX();
    }
}

private void updateCFDIReference_MX()
{
    SalesParmTable salesParmTableLocal;

    for (salesParmTableLocal = salesParmTable_ds.getFirst(); salesParmTableLocal; salesParmTableLocal = salesParmTable_ds.getNext())
    {
        EInvoiceCFDIReferenceParm_MX::initCustPrepayments(salesParmTableLocal, salesParmTableLocal.salesTable());
    }
}

