void  clicked()
{
    Args        args        = new Args();
    boolean     reArrangeOk = true;

    if (reArrangeNow  &&  salesParmUpdate.numberOfTables() > 1)
    {
        if (Box::yesNo("@SYS71216", DialogButton::Yes))
        {
            reArrangeOk = element.reArrange();
        }
    }

    if (reArrangeOk)
    {
        salesParmTable.researchSalesOrderAutoCharges();

        args.caller(element);

        args.parmEnumType(enumnum(SalesUpdate));
        args.parmEnum(enum2int(salesParmUpdate.SpecQty));

        args.record(salesParmTable);

        this.menufunction().run(args);
    }
}

void  clicked()
{
    Args        args        = new Args();
    boolean     reArrangeOk = true;

    if (reArrangeNow  &&  salesParmUpdate.numberOfTables() > 1)
    {
        if (Box::yesNo("@SYS71216", DialogButton::Yes))
        {
            reArrangeOk = element.reArrange();
        }
    }

    if (reArrangeOk)
    {
        args.caller(element);

        args.parmEnumType(enumNum(SalesUpdate));
        args.parmEnum(enum2int(salesParmUpdate.SpecQty));

        args.record(salesParmTable);

        this.menufunction().run(args);
    }
}

void clicked()
{
    boolean  reArrangeOk = true;

    if (reArrangeNow  &&  salesParmUpdate.numberOfTables() > 1)
    {
        if (Box::yesNo("@SYS75237", DialogButton::Yes))
        {
            reArrangeOk = element.reArrange();
        }
    }

    if (reArrangeOk)
    {
        salesParmTable.researchSalesOrderAutoCharges();

        SalesTotals_ParmTrans::showTax(menuitemDisplayStr(TaxTmpWorkSalesOrder), salesParmTable,salesParmUpdate.SpecQty
            , this.formRun()
            , documentStatus
            );

        if (Tax::isTaxIntegrationEnabledForBusinessProcess(TaxIntegrationBusinessProcess::Sales))
        {
            SalesParmLine_ds.reread();
            SalesParmLine_ds.refresh();
        }
    }
}

public void clicked()
{
    this.parameters(classStr(TaxableDocumentDescriptorSalesOrderParm));
    super();
}

void clicked()
{
    boolean  reArrangeOk = true;

    if (reArrangeNow  &&  salesParmUpdate.numberOfTables() > 1)
    {
        if (Box::yesNo("@SYS75237", DialogButton::Yes))
        {
            reArrangeOk = element.reArrange();
        }
    }

    if (reArrangeOk)
    {
        DeliverySlipTotals_Parm_BR::showTax(menuitemDisplayStr(TaxTransSourceDeliverySlip_BR), salesParmTable,salesParmUpdate.SpecQty);
    }
}

void clicked()
{
    CustVendPaymSched  custVendPaymSched;
    boolean            reArrangeOk = true;
    boolean            performedRearrange;

    if (reArrangeNow && salesParmUpdate.numberOfTables() > 1)
    {
        if (Box::yesNo("@SCM:ConsolidateInvoicesBeforePaymentScheduleIsShown", DialogButton::Yes))
        {
            performedRearrange = true;
            reArrangeOk = element.reArrange();
        }
    }

    if (Box::yesNo("@SCM:DatesInSetupTabWillBeUpdatedAfterThisOperation", DialogButton::Yes))
    {
        if (reArrangeOk && ! CustPaymSched::exist(salesParmTable.TableId, salesParmTable.RecId))
        {
            custVendPaymSched = CustVendPaymSched::construct(SysModule::Cust, salesParmTable);

            SalesTable localSalesTable = salesParmTable.salesTable();

            CustPaymSched custPaymSched = CustPaymSched::find(localSalesTable.TableId, localSalesTable.RecId);

            if (!performedRearrange && custPaymSched && custPaymSched.CopyPreciselyToInvoice)
            {
                custVendPaymSched.copyCustVendPaymSched(custPaymSched);
            }
            else
            {
                custVendPaymSched.copyPaymSched(PaymSched::find(element.retrievePaymentSchedule()));
                salesParmTable.createPaymentSched();
            }
        }

        if (reArrangeOk)
        {
            super();
        }

        if (! CustPaymSched::exist(salesParmTable.TableId,salesParmTable.RecId))
        {
            custVendPaymSched = CustVendPaymSched::construct(SysModule::Cust,salesParmTable);

            custVendPaymSched.copyPaymSched(PaymSched::find(element.retrievePaymentSchedule()));
            salesParmTable.createPaymentSched();
        }
    }
}

NoYes check(SalesParmTable _salesParmTable)
{
    if (SalesParmLine::existTableRefId(_salesParmTable.ParmId, _salesParmTable.TableRefId))
    {
        return NoYes::Yes;
    }

    return NoYes::No;
}

public void jumpRef()
{
    QueryBuildDataSource qBd;
    FormDataSource       formDataSource;
    QueryBuildRange      qBr;

	FormRun formRun = new MenuFunction(menuItemDisplayStr(CustPackingSlipJournal), MenuItemType::Display).create();
    formDataSource = formRun.dataSource();

    switch (documentStatus)
    {
        case DocumentStatus::Invoice, DocumentStatus::Facture_RU:
            formDataSource.query(CustPackingSlipJour::queryPackingSlipIdForUpdate_W(salesParmTable.ParmId,
                                                                                    salesParmTable.SalesId,
                                                                                    salesParmTable.TableRefId));
            break;

        case DocumentStatus::PackingSlip:
            formDataSource.query(CustPackingSlipJour::queryPackingSlipStorno_LT());
            break;
    }

    if (salesParmTable.PackingSlipIdForUpdate_W)
    {
        qBd = formDataSource.query().dataSourceTable(tableNum(CustPackingSlipJour));
        qBr = qBd.addRange(fieldNum(CustPackingSlipJour, PackingSlipId));
        qBr.value(salesParmTable.PackingSlipIdForUpdate_W);
    }

    formRun.run();
    formRun.detach();
}

public void lookup()
{
    switch (documentStatus)
    {
        case DocumentStatus::Invoice, DocumentStatus::Facture_RU:
            CustPackingSlipJour::lookupPackingSlipId_W(this,
                                                       CustPackingSlipJour::queryPackingSlipIdForUpdate_W(salesParmTable.ParmId,
                                                                                                          salesParmTable.SalesId,
                                                                                                          salesParmTable.TableRefId));
            break;
        case DocumentStatus::PackingSlip:
            CustPackingSlipJour::lookupPackingSlipId_W(this,
                                                       CustPackingSlipJour::queryPackingSlipStorno_LT());
            break;
    }
}

void clicked()
{
    super();

    CustVendPaymInvoiceWithJournal::promptOutPaym(ModuleCustVend::Cust, salesParmTable.salesTable().PaymMode);
}

/// <summary>
/// Validates invoice posting date value.
/// </summary>
/// <returns></returns>
public boolean validate()
{
    boolean ret;
    TransDate sysDate = DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone());

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoFR])
        && SalesParmTable_Transdate.dateValue() > sysDate)
    {
        Box::warning(strFmt("@ChronologicalNumbering:InvoiceDateLaterThanCurrentDate", SalesParmTable_Transdate.dateValue(), SalesParmTable.CustAccount,
                sysDate));
    }

    return super();
}

public boolean modified()
{
    boolean ret = super();

    CustPaymSched custPaymSched = CustPaymSched::find(salesParmTable.TableId, salesParmTable.RecId);

    if (custPaymSched)
    {
        warning("@AccountsReceivable:PaymSchedForSalesOrderInvoiceHasBeenCreated");
    }
    else
    {
        custPaymSched = CustPaymSched::find(salesParmTable.salesTable().TableId, salesParmTable.salesTable().RecId);

        if (custPaymSched && custPaymSched.CopyPreciselyToInvoice)
        {
            warning("@AccountsReceivable:PaymentSchedForSalesOrderInvoiceSOCopyToInvoiceSet");
        }
    }

    return ret;
}

