Method.Source:
    [Form]
    public class SalesHandlingExportFacture_RU extends FormRun
    {
         NoYes              cancelHandling;
         EndDate            endPeriodDate;
}

public void init()
{
    if (!element.args() || (element.args().parmEnumType() != enumNum(NoYes)))
    {
        throw error(Error::wrongUseOfFunction(funcName()));
    }

    super();

    cancelHandling = element.args().parmEnum();

    element.initDesign();
}

void initDesign()
{
    salesTmpFacturePreprocessing_DS.object(fieldNum(SalesTmpExportFactureProcessing_RU, ProcessingType)).visible(!cancelHandling);
    if (cancelHandling)
    {
        element.design().caption("@GLS112995");
    }
    else
    {
        element.design().caption("@GLS112994");
    }
}

void markAllFactures(NoYes _mark)
{
    SalesTmpExportFactureProcessing_RU common, localExportFactureProcessing;

    common.data(salesTmpFacturePreprocessing.data());

    salesTmpFacturePreprocessing_DS.refresh();
    salesTmpFacturePreprocessing_QR.reset();

    while (salesTmpFacturePreprocessing_QR.next())
    {
        localExportFactureProcessing = salesTmpFacturePreprocessing_QR.get(tableNum(SalesTmpExportFactureProcessing_RU));
        if (localExportFactureProcessing.Mark != _mark)
        {
            localExportFactureProcessing.Mark = _mark;
            localExportFactureProcessing.doUpdate();
        }
    }

    salesTmpFacturePreprocessing_DS.research();

    salesTmpFacturePreprocessing_DS.positionToRecord(common);

    salesTmpFacturePreprocessing_DS.reread();
}

void selectCancellation()
{
    SalesSelectCancellationExportFacture_RU cancelExportFactures;

    cancelExportFactures = SalesSelectCancellationExportFacture_RU::construct();

    if (cancelExportFactures.prompt())
    {
        cancelExportFactures.runOperation();
        element.deleteDSData();
        salesTmpFacturePreprocessing.setTmpData(cancelExportFactures.tmpFactureProcessing());
        salesTmpFacturePreprocessing_DS.research();
        element.enableButtons(salesTmpFacturePreprocessing.RecId ? true : false);
    }
}

void selectData()
{
    if (cancelHandling)
    {
        element.selectCancellation();
    }
    else
    {
        element.selectHandling();
    }
}

void selectHandling()
{
    SalesHandlingStatusExportFacture_RU handlingExportFactures;

    handlingExportFactures = SalesHandlingStatusExportFacture_RU::construct();

    if (handlingExportFactures.prompt())
    {
        handlingExportFactures.runOperation();
        endPeriodDate = handlingExportFactures.parmEndDate();
        element.deleteDSData();
        salesTmpFacturePreprocessing.setTmpData(handlingExportFactures.tmpFactureProcessing());
        salesTmpFacturePreprocessing_DS.research();
        element.enableButtons(salesTmpFacturePreprocessing.RecId ? true : false);
    }
}

void updateData()
{
    FactureProcessingExport_RU  processingExport;

    processingExport = FactureProcessingExport_RU::construct(salesTmpFacturePreprocessing, cancelHandling);
    processingExport.onPaymentConfirmationDate(endPeriodDate);
    processingExport.run();
    salesTmpFacturePreprocessing_DS.research();
    element.enableButtons(salesTmpFacturePreprocessing.RecId ? true : false);
}
