[Form]
public class SalesLine extends FormRun
{
    InventDimCtrl_Frm_Mov   inventDimFormSetup;
    boolean                 showProject;
    boolean                 salesReturnMode;

    // <GIN>
    boolean                 indiaCountryCheck;
    #ISOCountryRegionCodes
    // </GIN>

    InventTrackingShippedSalesSummaryFilter traceFilter;
    InventTrackingOpenSalesSummaryFilter traceFilterOpen;
}

/// <summary>
///    Enables and disables fields for when this form is called from InventTrackingDimTracing form.
/// </summary>
/// <remarks>
///    Is specific to Inventory tracing.
/// </remarks>
private void disableEnableControlsForItemTracing()
{
    //enable customer related fields
    SalesLine_CustAccount.visible(true);
    editContactPersonName1.visible(true);

    //enable fields related to quantity
    SalesLineGrid_RemainSalesFinancial.visible(true);
    SalesLineGrid_PdsCWRemainInventFinancial.visible(true);

    // Depending on whether tracing shipped or open sales orders delivered or remaining quanties is shown
    if (!traceFilterOpen)
    {
        SalesLineGrid_DeliveredInTotal.visible(true);
        SaleslineGrid_pdsCWDeliveredInTotal.visible(true);
    }
    else
    {
        SalesLineGrid_RemainSalesPhysical.visible(true);
        SalesLineGrid_PdsCWRemainInventPhysical.visible(true);
    }

    //enable sales line status
    SalesLine_SalesStatus.visible(true);
    SalesTable_ReturnItemNum.visible(true);
    SalesLineGrid_ConfirmedDlv.visible(true);
    SalesLine_InterCompanyOriginDisplay.visible(true);

    //disable sales category
    SalesLine_SalesCategory.visible(false);

    //disable price related fields except for net amount
    SalesLine_SalesPrice.visible(false);
    SalesLine_LineDiscGrid.visible(false);
    SalesLine_LinePercentGrid.visible(false);
}

public Common docCursor()
{
    Common docCursor;

    docCursor = super();

    if (docCursor.TableId == tableNum(InventDim))
    {
        docCursor = salesLine;
    }

    return docCursor;
}

public void  init()
{
    traceFilter = element.args().parmObject() as InventTrackingShippedSalesSummaryFilter;
    traceFilterOpen = element.args().parmObject() as InventTrackingOpenSalesSummaryFilter;

    if (traceFilter)
    {
        traceFilter.parmFormRun(this);
    }

    switch (element.args().menuItemName())
    {
        case menuitemDisplayStr(SalesLineProject):
            if (element.args().parmEnum())
            {
                showProject = true;
            }
            break;
        case menuitemDisplayStr(SalesLineReturnLine):
            salesReturnMode = true;
            break;
        default : break;
    }

    super();

    if (traceFilter)
    {
        //Initialization specifically for item tracing
        traceFilter.formInitPost();
        this.disableEnableControlsForItemTracing();
    }

    // Initialization specifically for projects
    element.project();

    element.updateDesign(InventDimFormDesignUpdate::Init);

    if (#PdsCatchWeightEnabled)
    {
        PdsCWMain.visible(true);
        CWQtyRow.visible(true);
    }

    // <GTE>
    if (TaxIntegrationUtils::isTaxInformationEnabled())
    {
        TransTaxInformation.visible(true);
    }
    // <GTE>
}

Object inventDimSetupObject()
{
    return inventDimFormSetup;
}

void project()
{
    if (!showProject)
        return;

    if (! element.args().record()) // called from menu
        salesLine_ds.query().dataSourceTable(tableNum(SalesLine)).addRange(fieldNum(SalesLine, ProjId)).value(SysQuery::valueNotEmptyString());
}

void updateDesign(InventDimFormDesignUpdate mode)
{
    InventMovement localMovement = InventMovement::construct(salesLine);

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init :
            if (!inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl_Frm_Mov::newFromFormMovement(element, localMovement);
            }

        case InventDimFormDesignUpdate::Active :
            inventDimFormSetup.formActiveSetup(localMovement.inventDimGroupSetup(),localMovement);
            inventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange :
            inventDimFormSetup.formActiveSetup(localMovement.inventDimGroupSetup(),localMovement);
            inventDimFormSetup.formSetControls(false);
            break;

        default : throw error(strFmt("@SYS54195",funcName()));
    }
    salesLine_ds.object(fieldNum(SalesLine, ProjCategoryId)).visible(showProject);
    salesLine_ds.object(fieldNum(SalesLine, ProjId)).visible(showProject);
    salesLine_ds.object(fieldNum(SalesLine, SalesQty)).visible(!salesReturnMode);
    salesLine_ds.object(fieldNum(SalesLine, ExpectedRetQty)).visible(salesReturnMode);
}

int  active()
{
    int ret;

    ret = super();

    element.updateDesign(InventDimFormDesignUpdate::Active);

    return ret;
}

void linkActive()
{
    boolean                 ret = false;
    str                     projIdstr;

    ProjInvoiceTable        projInvoiceTable;
    ProjTable               projTable, tmpProjTable;
    ProjInvoiceControl      projInvoiceControl;
    ProjWIPTable            projWIPTable;

    SalesAgreementHeader    salesAgreementHeader;
    AgreementLine           agreementLine;

    QueryBuildDataSource    queryBuildDataSource;

    ProdTable               prodTable;
    SalesLine               localSalesLine;

    switch (element.args().dataset())
    {
        case tableNum(ProjInvoiceTable)  :  projInvoiceTable   =   element.args().record();

            queryBuildDataSource = this.query().dataSourceTable(tableNum(SalesLine));
            queryBuildDataSource.clearRanges();

            while select ProjId from projTable
                                                where projTable.ProjInvoiceProjId   == projInvoiceTable.ProjInvoiceProjId
            {
                ret = true;
                queryBuildDataSource.addRange(fieldNum(SalesLine,ProjId)).value(projTable.ProjId);
            }

            if (!ret)
            {
                queryBuildDataSource.addRange(fieldNum(SalesLine, ProjId)).value(SysQuery::valueNotEmptyString());
            }
            break;

        case tableNum(ProjTable)            :  tmpProjTable   =   element.args().record();

            queryBuildDataSource = this.query().dataSourceTable(tableNum(SalesLine));
            queryBuildDataSource.clearRanges();

            projIdStr = tmpProjTable.ProjId;

            projIdStr += ('*');

            queryBuildDataSource.addRange(fieldNum(SalesLine,ProjId)).value(projIdStr);

            break;

        case tableNum(ProjWIPTable)     :   projWIPTable   =   element.args().record();

            queryBuildDataSource = this.query().dataSourceTable(tableNum(SalesLine));
            queryBuildDataSource.clearRanges();

            while select ProjId from projTable
                                                where projTable.wipProject  == projWIPTable.ProjId
            {
                ret = true;
                queryBuildDataSource.addRange(fieldNum(SalesLine,ProjId)).value(projTable.ProjId);
            }

            if (ret == false)
            {
                queryBuildDataSource.addRange(fieldNum(SalesLine, ProjId)).value(SysQuery::valueNotEmptyString());
            }
            break;

        case tableNum(ProdTable)            :  prodTable   =   element.args().record();

            queryBuildDataSource = this.query().dataSourceTable(tableNum(SalesLine));
            queryBuildDataSource.clearRanges();
            queryBuildDataSource.addRange(fieldNum(SalesLine,InventRefId)).value(prodTable.ProdId);

            break;

        case tableNum(TmpProjInvoiceControl):
            projInvoiceControl  = element.args().parmObject();

            queryBuildDataSource = this.query().dataSourceTable(tableNum(SalesLine));
            queryBuildDataSource.clearRanges();

            if (projInvoiceControl.parmProjId())
                                                queryBuildDataSource.addRange(fieldNum(SalesLine,ProjId)).value(projInvoiceControl.parmProjId());
            if (projInvoiceControl.parmProjCategoryId())
                                                queryBuildDataSource.addRange(fieldNum(SalesLine,ProjCategoryId)).value(projInvoiceControl.parmProjCategoryId());

            queryBuildDataSource.addRange(fieldNum(SalesLine,ConfirmedDlv)).value(projInvoiceControl.queryFromDate()+'..'+projInvoiceControl.queryToDate());
            queryBuildDataSource.addRange(fieldNum(SalesLine,SalesType)).value(SalesQueryRangeUtil::salesTypeCustomerDemand());

            break;
        case tableNum(SalesAgreementHeader):
            salesAgreementHeader = element.args().record();
            queryBuildDataSource = this.query().dataSourceTable(tableNum(SalesLine));
            queryBuildDataSource.clearRanges();

            queryBuildDataSource = this.query().dataSourceTable(tableNum(AgreementLine));
            queryBuildDataSource.clearDynalinks();
            queryBuildDataSource.relations(true);
            queryBuildDataSource.addDynalink(fieldNum(AgreementLine,Agreement),
                                                                             salesAgreementHeader,
                                                                             fieldNum(SalesAgreementHeader, RecId));
            break;

        case tableNum(AgreementLine):
            agreementLine = element.args().record();
            queryBuildDataSource = this.query().dataSourceTable(tableNum(SalesLine));
            queryBuildDataSource.clearRanges();

            queryBuildDataSource = this.query().dataSourceTable(tableNum(AgreementLineReleasedLine));
            queryBuildDataSource.clearDynalinks();
            queryBuildDataSource.relations(true);
            queryBuildDataSource.addDynalink(fieldNum(AgreementLineReleasedLine, AgreementLine),
                                                                             agreementLine,
                                                                             fieldNum(AgreementLine, RecId));
            break;

        case tableNum(SalesLine):
            if (salesReturnMode)
            {
                localSalesLine = element.args().record();
                queryBuildDataSource = this.query().dataSourceTable(tableNum(SalesLine));
                queryBuildDataSource.clearRanges();
                queryBuildDataSource.addRange(fieldNum(SalesLine, InventTransIdReturn)).value(queryValue(localSalesLine.InventTransId));
                queryBuildDataSource.addRange(fieldNum(SalesLine, SalesType)).value(queryValue(SalesType::ReturnItem));
            }
            break;
        default:
            TradeCustomizationsEventHandler::salesLineLinkActive(this);
            break;
    }

    super();
}

void  init()
{
    QueryBuildDataSource    queryBuildDataSource;

    super();

    queryBuildDataSource = this.query().dataSourceTable(tableNum(SalesLine));

    switch (element.args().dataset())
    {
        case tableNum(SalesLine)        :
        case tableNum(ProjTable)        :
        case tableNum(ProdTable)        :
            queryBuildDataSource.clearDynalinks();
            break;

        case tableNum(SalesAgreementHeader)     :
        case tableNum(AgreementLine)            :
            queryBuildDataSource.clearDynalinks();

            // We need to add extra data source...
            queryBuildDataSource = this.query().dataSourceTable(tableNum(SalesLine));
            queryBuildDataSource = queryBuildDataSource.addDataSource(tableNum(AgreementLineReleasedLine));
            queryBuildDataSource.joinMode(JoinMode::ExistsJoin);
            queryBuildDataSource.addLink(fieldNum(SalesLine, InventTransId),
                                         fieldNum(AgreementLineReleasedLine, SalesLineInventTransId));
            queryBuildDataSource.addLink(fieldNum(SalesLine, DataAreaId),
                                         fieldNum(AgreementLineReleasedLine, SalesLineDataAreaId));

            if (element.args().dataset() == tableNum(SalesAgreementHeader))
            {
                // In case we're querying lines for the whole Agreement we need add yet another data source...
                queryBuildDataSource = queryBuildDataSource.addDataSource(tableNum(AgreementLine));
                queryBuildDataSource.joinMode(JoinMode::ExistsJoin);
                queryBuildDataSource.addLink(fieldNum(AgreementLineReleasedLine, AgreementLine),
                                             fieldNum(AgreementLine, RecId));
            }
            break;
    }

    if (traceFilter)
    {
        traceFilter.parmMainDataSource(SalesLine_ds);
        traceFilter.parmFilterDataSource(SalesLineTmpFilter_ds);
        traceFilter.parmSalesTableDataSource(SalesTable_ds);
        traceFilter.dataSourceInitPost();
        salesLine_ds.cacheAddMethod(tableMethodStr(SalesTable, editContactPersonName));
    }

    salesLine_ds.cacheAddMethod(tableMethodStr(SalesLine, invoicedInTotal));
    salesLine_ds.cacheAddMethod(tableMethodStr(SalesLine, deliveredInTotal));
    salesLine_ds.cacheAddMethod(tableMethodStr(SalesLine, pickedInTotalSalesUnit));
    salesLine_ds.cacheAddMethod(tableMethodStr(SalesLine, reservedPhysicalInSalesUnit));
    salesLine_ds.cacheAddMethod(tableMethodStr(SalesLine, reservedOnOrderInSalesUnit));
    salesLine_ds.cacheAddMethod(tableMethodStr(SalesLine, onOrderInSalesUnit));
}

public void jumpRef()
{
    if (salesReturnMode)
    {
        SalesTable::jumpRefSalesIdReturnTable(salesLine.SalesId);
    }
    else
    {
        super();
    }
}

