[Form]
public class SalesLineBackOrder extends FormRun implements SysIFilterConsumerForm
{
    QueryBuildRange             backorderDateSalesLine;
    InventDimCtrl_Frm_Mov       inventDimFormSetup;
    QueryFilter                 backorderDatefilter;

}

public Common docCursor()
{
    Common docCursor;

    docCursor = super();

    if (docCursor.TableId == tableNum(InventDim))
    {
        docCursor = salesLine;
    }

    return docCursor;
}

void init()
{
    date initialDateValue;

    if (element.args().caller() is SysIFilterProvider)
    {
        element.args().lookupRecord(null);
        element.args().record(null);
    }

    super();

    salesLine_ds.query().dataSourceTable(tablenum(SalesLine)).clearRange(fieldnum(SalesLine, ShippingDateRequested));

    element.mcrSetRangeValue();

    initialDateValue = DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()) - 1;
    if (element.args() && element.args().menuItemName() != menuitemDisplayStr(SalesDelayedOrders))
    {
        int backOrderDays = real2int(MCROrderParameters::find().BackorderDays);
        if (backOrderDays > 1)
        {
            initialDateValue -= (backorderDays -1);
        }
    }

    backorderDate.dateValue(initialDateValue);

    element.updateDesign(InventDimFormDesignUpdate::Init);

    if (#PdsCatchWeightEnabled)
    {
        SalesLine_PdsCWQty.visible(true);
        CWQtyRow.visible(true);
    }
}

public void mcrSetRangeValue()
{
    str         custAccounts;
    CustTable   custTable;
    CustTable   custTableBuffer;

    if (isConfigurationkeyEnabled(configurationKeyNum(MCRCallCenter))
        && element.args().dataset() == tableNum(CustTable))
    {
        // Not only show backorder lines (sales line query) for the current customer but any accounts
        // that were merged into the current customer.
        custTable = element.args().record();

        custAccounts = SysQuery::value(custTable.AccountNum);

        while select AccountNum from custTableBuffer
            where custTableBuffer.MCRMergedRoot == custTable.AccountNum
        {
            custAccounts += strfmt(",%1",SysQuery::value(custTableBuffer.AccountNum));
        }

        salesLine_ds.queryBuildDataSource().addRange(fieldNum(SalesLine, CustAccount)).value(custAccounts);
    }
}

void updateDesign(InventDimFormDesignUpdate mode)
{
    InventMovement movement = InventMovement::construct(salesLine);

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init :
            if (!inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl_Frm_Mov::newFromFormMovement(element, movement);
            }

        case InventDimFormDesignUpdate::Active :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(),movement);
            inventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(),movement);
            inventDimFormSetup.formSetControls(false);
            break;

        default : throw error(strFmt("@SYS54195",funcName()));
    }
}

public void init()
{
    super();

    if (!element.args() || element.args().menuItemName() != menuitemDisplayStr(SalesDelayedOrders))
    {
        this.query().dataSourceTable(tableNum(HcmWorker)).enabled(false);
    }
}

void executeQuery()
{
    QueryBuildDataSource qbds = salesLine_ds.query().dataSourceTable(tablenum(SalesLine));
    backorderDatefilter = SysQuery::findOrCreateQueryFilter(salesLine_ds.query(), qbds, fieldStr(SalesLine, ConfirmedDlv));
    backorderDatefilter.value(strFmt('..%1',backOrderDate.dateValue()));

    super();
}

boolean modified()
{
    boolean ret;

    ret = super();

    salesLine_ds.executeQuery();

    return ret;
}
