/// <summary>
/// Creates and initializes the sales order copying contract object.
/// </summary>
/// <param name = "_salesOrderCopyingContract">A <c>SalesOrderCopyingContract</c> instance to initialize.</param>
/// <returns>An instance of <c>SalesOrderCopyingContract</c>.</returns>
protected SalesOrderCopyingContract createSalesOrderCopyingContract(SalesTable _salesTable)
{
    SalesOrderCopyingContract salesOrderCopyingContract = SalesOrderCopyingContract::construct();

    this.initializeSalesOrderCopyingContract(salesOrderCopyingContract, _salesTable);

    return salesOrderCopyingContract;
}

/// <summary>
/// Initializes the <c>SalesOrderCopyingContract</c>.
/// </summary>
/// <param name = "_salesOrderCopyingContract">A <c>SalesOrderCopyingContract</c> instance to initialize.</param>
protected void initializeSalesOrderCopyingContract(SalesOrderCopyingContract _salesOrderCopyingContract, SalesTable _salesTable)
{
    NoYes           recalculateAmount;
    Qty             qtyFactor;

    switch (copyItemQtyPrice.selection())
    {
        case ItemQtyPrice::Item :
            recalculateAmount   = NoYes::Yes;
            qtyFactor           = 0;
            break;

        case ItemQtyPrice::ItemQty :
            recalculateAmount   = NoYes::Yes;
            qtyFactor           = 1;
            break;

        case ItemQtyPrice::ItemQtyPrice :
            recalculateAmount   = NoYes::No;
            qtyFactor           = 1;
            break;
    }

    _salesOrderCopyingContract.parmCallingTableSalesId(_salesTable.SalesId);
    _salesOrderCopyingContract.parmTmpFrmVirtualLines(tmpFrmVirtualLines);
    _salesOrderCopyingContract.parmQtyFactor(qtyFactor);
    _salesOrderCopyingContract.parmReverseSign(NoYes::No);
    _salesOrderCopyingContract.parmRecalculateAmount(recalculateAmount);
    _salesOrderCopyingContract.parmStartingLineNum(callerSalesLine.LineNum + 0.01);
    _salesOrderCopyingContract.parmSalesPurchCopy(SalesPurchCopy::CopyAllLines);

}

boolean canClose()
{
    boolean canClose = super();

    try
    {
        if (element.closedOk() && canClose)
        {
            SalesTable salesTable = element.args().caller().salesTable();

            if (callerDataSource.allowEdit() && salesTable.checkUpdate())
            {
                SalesOrderCopyingContract salesOrderCopyingContract = this.createSalesOrderCopyingContract(salesTable);

                SalesCopying::copyServer(salesOrderCopyingContract.pack(), false);

                callerDataSource.executeQuery();
            }
        }
    }
    catch
    {
        canClose = false;
    }

    return canClose;
}

NoYes  editCopy(boolean             _set,
                RefTableId          _tableId,
                RefRecId            _recId,
                Num                 _id,
                LineNum             _lineNum,
                TransDate           _transDate,
                Qty                 _qty,
                NoYes               _copy
                , PdsCWInventQty      _cwQty
                )
{
    NoYes  copy;

    select tmpFrmVirtualLines
        where tmpFrmVirtualLines.TableNum    == _tableId &&
              tmpFrmVirtualLines.RecordNo    == _recId;

    if (tmpFrmVirtualLines)
    {
        copy   = NoYes::Yes;
    }

    if (_set)
    {
        if (_copy)
        {
            tmpFrmVirtualLines.TableNum      = _tableId;
            tmpFrmVirtualLines.RecordNo      = _recId;
            tmpFrmVirtualLines.LineNum       = _lineNum;
            tmpFrmVirtualLines.Id            = _id;
            tmpFrmVirtualLines.TransDate     = _transDate;
            tmpFrmVirtualLines.Qty           = _qty;
            tmpFrmVirtualLines.PdsCWQty     = _cwQty;

            tmpFrmVirtualLines.write();
            copy   = NoYes::Yes;
        }
        else
        {
            tmpFrmVirtualLines.delete();
            copy   = NoYes::No;
        }
    }

    return copy;
}

void  init()
{
    if (!element.args().record())
    {
        throw error("@SYS22539");
    }

    super();

    element.updateDesign(InventDimFormDesignUpdate::Init);

    copyItemQtyPrice.selection(ItemQtyPrice::ItemQty);

    callerSalesLine     = element.args().record();
    callerDataSource    = FormDataUtil::getFormDataSource(callerSalesLine);
}

Object inventDimSetupObject()
{
    return inventDimFormSetup;
}

void updateDesign(InventDimFormDesignUpdate mode)
{
    InventMovement movement = InventMovement::construct(salesLine);

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init :
        case InventDimFormDesignUpdate::LinkActive :
             inventDimFormSetup  = InventDimCtrl_Frm_Mov::newFromFormMovement(element, movement);

        case InventDimFormDesignUpdate::Active :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(),movement);
            inventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(),movement);
            inventDimFormSetup.formSetControls(false);
            break;

        default : throw error(strfmt("@SYS54195",funcname()));
    }
}

void init()
{
    QueryBuildDataSource queryBuildDataSource;

    super();

    queryBuildDataSource = this.query().dataSourceTable(this.table());
    queryBuildDataSource.clearDynalinks();
    rangeCustInvoiceTrans_ItemId        = queryBuildDataSource.addRange(fieldnum(CustInvoiceTrans, ItemId));
    rangeCustInvoiceTrans_SalesCategory = queryBuildDataSource.addRange(fieldnum(CustInvoiceTrans, SalesCategory));
    rangeCustInvoiceTrans_InvoiceId     = queryBuildDataSource.addRange(fieldnum(CustInvoiceTrans, InvoiceId));

    custInvoiceTrans_ds.cacheAddMethod(tableMethodStr(CustInvoiceTrans, PdsCWUnitId));
}

void executeQuery()
{
    InventDim       inventDim;
    InventDimParm   inventDimParm;

    if (callerSalesLine.isCategoryBased())
    {
        rangeCustInvoiceTrans_ItemId.value(SysQuery::valueEmptyString());
        rangeCustInvoiceTrans_SalesCategory.enabled(true);
        rangeCustInvoiceTrans_SalesCategory.value(queryValue(callerSalesLine.SalesCategory));
    }
    else
    {
        rangeCustInvoiceTrans_SalesCategory.enabled(false);
        rangeCustInvoiceTrans_ItemId.value(queryValue(callerSalesLine.ItemId));

        inventDim = callerSalesLine.inventDim();
        inventDimParm = EcoResProductDimGroupSetup::newItemId(callerSalesLine.ItemId).inventDimParmActiveDimensions();
        inventDimParm.queryAddRangeValue(this.query().dataSourceName(identifierstr(InventDimInvoiceTrans)),inventDim);
    }

    rangeCustInvoiceTrans_InvoiceId.value(SysQuery::valueNotEmptyString());

    rangeCustInvoiceJour_OrderAccount.value(queryValue(callerSalesLine.CustAccount));
    rangeCustInvoiceJour_RefNum.value(enum2Value(RefNum::SalesOrder));

    this.query().dataSourceTable(tablenum(CustInvoiceTrans)).addSortField(fieldnum(CustInvoiceTrans, InvoiceId));
    this.query().dataSourceTable(tablenum(CustInvoiceTrans)).addSortField(fieldnum(CustInvoiceTrans, LineNum));
    this.query().dataSourceTable(tablenum(CustInvoiceTrans)).addSortField(fieldnum(CustInvoiceTrans, ItemId));

    super();
}

int active()
{
    int             ret;

    ret = super();

    custInvoiceTrans_ds.allowEdit    (callerDataSource.allowEdit());

    return ret;
}

