    element.updateDesign(InventDimFormDesignUpdate::LinkActive);
}

void init()
{
    QueryBuildDataSource queryBuildDataSource;

    super();

    queryBuildDataSource = this.query().dataSourceTable(this.table());
    queryBuildDataSource.clearDynalinks();
    rangeSalesLine_SalesStatus  = queryBuildDataSource.addRange(fieldnum(SalesLine, SalesStatus));
    rangeSalesLine_ItemId       = queryBuildDataSource.addRange(fieldnum(SalesLine, ItemId));
    rangeSalesLine_SalesCategory= queryBuildDataSource.addRange(fieldnum(SalesLine, SalesCategory));
    rangeSalesLine_CustAccount  = queryBuildDataSource.addRange(fieldnum(SalesLine, CustAccount));

    this.cacheAddMethod(tablemethodStr(SalesLine,PdsCWUnitId));
}

void executeQuery()
{
    InventDim       inventDim;
    InventDimParm   inventDimParm;
    SalesTable      salesTable = element.args().caller().salesTable();

    rangeSalesLine_SalesStatus.value(strfmt('%1,%2',enum2Value(SalesStatus::Backorder),enum2Value(SalesStatus::Delivered)));

    if (callerSalesLine.isCategoryBased())
    {
        rangeSalesLine_ItemId.value(SysQuery::valueEmptyString());
        rangeSalesLine_SalesCategory.enabled(true);
        rangeSalesLine_SalesCategory.value(queryValue(callerSalesLine.SalesCategory));
    }
    else
    {
        rangeSalesLine_SalesCategory.enabled(false);
        rangeSalesLine_ItemId.value(queryValue(callerSalesLine.ItemId));

        inventDim = callerSalesLine.inventDim();
        inventDimParm = EcoResProductDimGroupSetup::newItemId(callerSalesLine.ItemId).inventDimParmActiveDimensions();
        inventDimParm.queryAddRangeValue(this.query().dataSourceName(identifierstr(InventDimSalesLine)),inventDim);
    }

    rangeSalesLine_CustAccount.value(queryValue(salesTable.CustAccount));

    this.query().dataSourceTable(tablenum(SalesLine)).addSortField(fieldnum(SalesLine, SalesId));
    this.query().dataSourceTable(tablenum(SalesLine)).addSortField(fieldnum(SalesLine, LineNum));
    this.query().dataSourceTable(tablenum(SalesLine)).addSortField(fieldnum(SalesLine, ItemId));

    super();
}

int active()
