[Form]
public class SalesLineDeliveryDetails extends FormRun implements IChartClickable, ReqTransExplosionSetupInterface
{
    SalesLine                                   argsSalesLine;
    LogisticsPostalAddress                      postalAddress;
    boolean                                     isATP;
    boolean                                     isCTP;
    boolean                                     isSalesLeadTime;
    ReqTransFormExplosion                       reqTransFormCTPExplosion;
    SalesTmpDeliverySuggestionBuilder           suggestionBuilder;
    SalesLineDeliveryDetailsManager             deliveryDetailsManager;
    EcoResProductDimensionsLookupController     alternativeProductDimensionsController;
    InventDimParm                               dimParmActive;

}

public void init()
{
    argsSalesLine = element.args().record() ? element.args().record() : element.args().lookupRecord();

    select firstonly CountryRegionId, State, County, ZipCode
        from postalAddress
            where postalAddress.RecId == argsSalesLine.DeliveryPostalAddress;

    super();

    this.handleSalesLineWithDeliveryDateControlTypeMpsCTPOnFormInit();

    DeliveryAlternativesDeliveryMode.text(argsSalesLine.DlvMode);

    this.initAlternativeProductDimensionsLookup();
    this.setProductDimensionsVisibility();
    this.updateInventDimVisibility();
    this.populateDeliverySuggestions();
    this.updateLayoutForSelectedDeliveryDateControlType();

    SalesLineOverviewTab.caption(strFmt("@SCM:SalesDeliveryAlternativesProductOverviewCaption", argsSalesLine.ItemId, argsSalesLine.itemName()));
}

private void handleSalesLineWithDeliveryDateControlTypeMpsCTPOnFormInit()
{
    if (!argsSalesLine.isDeliveryDateControlTypeMpsCTP())
    {
        return;
    }

    argsSalesLine.DeliveryDateControlType = SalesDeliveryDateControlType::ATP;

    warning("@SCM:MPSFullRunCTPDeliveryAlternativesFormWarning");
}

private void initAlternativeProductDimensionsLookup()
{
    if (argsSalesLine.isDeliveryDateControlTypeClassicCTP())
    {
        AlternativeProductDimensionsLookupGroup.visible(false);
    }
    else
    {
        alternativeProductDimensionsController = EcoResProductDimensionsLookupController::newFromParentGroupControl(AlternativeProductDimensionsLookupGroup);
        alternativeProductDimensionsController.setLabel("@SCM:SalesDeliveryAlternativeIncludeOtherProductVariants");
        alternativeProductDimensionsController.initForItemId(argsSalesLine.ItemId);
        alternativeProductDimensionsController.modified += eventhandler(this.alternativeProductDimensionsLookupController_modified);
    }
}

private void alternativeProductDimensionsLookupController_modified()
{
    this.populateDeliverySuggestionsAndResearch();
}

private void updateLayoutForSelectedDeliveryDateControlType()
{
    isATP = argsSalesLine.DeliveryDateControlType == SalesDeliveryDateControlType::ATP
                || argsSalesLine.DeliveryDateControlType == SalesDeliveryDateControlType::ATPPlusIssueMargin;

    ATPChartGroup.visible(isATP);
    ATPTimeFence.visible(isATP);
    AvailableToday_ATPQty.visible(isATP);

    isCTP = argsSalesLine.isDeliveryDateControlTypeClassicCTP();

    CTPTreeGroup.visible(isCTP);

    boolean isIntercompanyOrder = argsSalesLine.isInterCompanyOrder();
    if (alternativeProductDimensionsController)
    {
        alternativeProductDimensionsController.getSingleProductDimensionCheckBox().enabled(!isIntercompanyOrder);
    }

    isSalesLeadTime = argsSalesLine.DeliveryDateControlType == SalesDeliveryDateControlType::SalesLeadTime;
    SalesLeadTime.visible(isCTP || isSalesLeadTime);
    IncludePartialQuantityCheckBox.enabled(!isIntercompanyOrder);
    IncludePartialQuantityCheckBox.visible(isATP);
    IncludeProcurementAlternatives.visible(isATP);
    ProcurementDeliveryTypeFilter.visible(isATP);
}

private SalesTmpDeliverySuggestionFilter suggestionsFilter()
{
    SalesTmpDeliverySuggestionFilter filter = SalesTmpDeliverySuggestionFilter::construct();

    if (alternativeProductDimensionsController)
    {
        filter.AlternativeProductVariantsDimParm = alternativeProductDimensionsController.selectedProductDimParm();
    }

    filter.IncludeLaterDates = IncludeLaterDatesCheckBox.checked();
    filter.IncludePartialQuantity = IncludePartialQuantityCheckBox.checked();
    filter.DeliveryModeId = DeliveryAlternativesDeliveryMode.Text();
    filter.IncludeProcurementAlternatives = IncludeProcurementAlternatives.checked();
    filter.ProcurementDeliveryType = any2Enum(ProcurementDeliveryTypeFilter.selection());
    return filter;
}

private void populateDeliverySuggestions()
{
    suggestionBuilder = SalesTmpDeliverySuggestionBuilder::construct(argsSalesLine);
    var deliveryAlternatives = suggestionBuilder.populateDeliveryAlternatives(this.suggestionsFilter());
    SalesTmpDeliverySuggestions.setTmpData(deliveryAlternatives);
}

private void populateDeliverySuggestionsAndResearch()
{
    this.populateDeliverySuggestions();
    SalesTmpDeliverySuggestions_ds.research();
}

public void redrawTab()
{
    if (reqTransFormCTPExplosion)
    {
        reqTransFormCTPExplosion.treeRedraw();
        CTPTree.expand(CTPTree.getRoot());
    }
}

public ReqTransFormExplosion reqTransForm()
{
    return reqTransFormCTPExplosion;
}

private InventDimParm activeProductDimensions()
{
    return InventDimParm::andParms(dimParmActive.data(), InventDimParm::allProductDim());
}

private InventDimParm suggestionsDimensions(InventDimParm _activeProductDimensions = this.activeProductDimensions())
{
    return InventDimParm::orParms(_activeProductDimensions, InventDimParm::siteAndWarehouse());
}

private void updateInventDimVisibility()
{
    InventDimParm activeProductDimensions = this.activeProductDimensions();

    InventDimCtrl_Frm inventDimFormSetupSalesLine = InventDimCtrl::newFromFormDataSource(InventDimSuggestions_ds);
    inventDimFormSetupSalesLine.parmDimParmVisibleGrid(activeProductDimensions);
    inventDimFormSetupSalesLine.parmDimParmVisible(activeProductDimensions);
    inventDimFormSetupSalesLine.formSetControls(true);

    InventDimParm suggestionsDimensions = this.suggestionsDimensions(activeProductDimensions);
    InventDimCtrl_Frm inventDimFormSetupSuggestions = InventDimCtrl::newFromFormDataSource(InventDimSalesLine_ds);
    inventDimFormSetupSuggestions.parmDimParmVisible(suggestionsDimensions);
    inventDimFormSetupSuggestions.formSetControls(true);
}

[SuppressBpWarning('BPParameterNotUsed', 'Parameter required by the implemented interface')]
public boolean drillThroughClicked(Args _args, MenuFunction _mf)
{
    Map map = _args.parmObject();

    map.insert(SalesLineDeliveryATPChartReqTransArgs::ArgsKeyItemId, argsSalesLine.ItemId);
    map.insert(SalesLineDeliveryATPChartReqTransArgs::ArgsKeyInventDimId, SalesTmpDeliverySuggestions.InventDimId);
    map.insert(SalesLineDeliveryATPChartReqTransArgs::ArgsKeyAtpTimeFence, ATPTimeFence.valueStr());
    map.insert(SalesLineDeliveryATPChartReqTransArgs::ArgsKeyCompanyId, SalesTmpDeliverySuggestions.SourcingCompanyId);
    map.insert(SalesLineDeliveryATPChartReqTransArgs::ArgsKeySourcingSiteId, SalesTmpDeliverySuggestions.SourcingInventSiteId);
    map.insert(SalesLineDeliveryATPChartReqTransArgs::ArgsKeySourcingWarehouseId, SalesTmpDeliverySuggestions.SourcingInventLocationId);
    map.insert(SalesLineDeliveryATPChartReqTransArgs::ArgsKeySalesLineInventTransId, argsSalesLine.InventTransId);

    return true;
}

public boolean canClose()
{
    this.handleSalesLineWithDeliveryDateControlTypeMpsCTPOnFormClose();

    boolean ret = super();

    if (ret && element.closedOk())
    {
        deliveryDetailsManager = SalesLineDeliveryDetailsManager::construct(SalesLine);
        deliveryDetailsManager.applySuggestion(SalesTmpDeliverySuggestions, this.suggestionsDimensions(), this.suggestionsFilter().lockedDimensions(argsSalesLine));

        ret = deliveryDetailsManager.canCommitChanges();
    }

    if (!ret)
    {
        this.handleSalesLineWithDeliveryDateControlTypeMpsCTPOnFormCloseFailure();
    }

    return ret;
}

private void handleSalesLineWithDeliveryDateControlTypeMpsCTPOnFormClose()
{
    if (!SalesLine.isDeliveryDateControlTypeMpsCTP())
    {
        return;
    }

    // Reset the delivery date type before committing changes.
    argsSalesLine.DeliveryDateControlType = salesLine.DeliveryDateControlType;
}

private void handleSalesLineWithDeliveryDateControlTypeMpsCTPOnFormCloseFailure()
{
    if (!SalesLine.isDeliveryDateControlTypeMpsCTP())
    {
        return;
    }

    // Set the delivery date type back to ATP in case the form cannot be closed.
    argsSalesLine.DeliveryDateControlType = SalesDeliveryDateControlType::ATP;
}

public void closeOk()
{
    super();

    if (this.closedOk())
    {
        SalesLine.SkipDefaultingSourcingVendor = NoYes::Yes;
        deliveryDetailsManager.commitChanges();

        FormDataSource salesLineDataSource = FormDataUtil::getFormDataSource(argsSalesLine);

        if (salesLineDataSource)
        {
            int pos = salesLineDataSource.getPosition();
            salesLineDataSource.research();
            salesLineDataSource.setPosition(pos);
            salesLineDataSource.rereadJoinHierarchy();
        }
    }
}

protected void setProductDimensionsVisibility()
{
    #InventDimDevelop

    InventDimGroupSetup::newItemId(argsSalesLine.ItemId).inventDimParmActive(dimParmActive);

    ProductImpactConfigurationRowGroup.visible(dimParmActive.ConfigIdFlag);
    ProductImpactSizeRowGroup.visible(dimParmActive.InventSizeIdFlag);
    ProductImpactColorRowGroup.visible(dimParmActive.InventColorIdFlag);
    ProductImpactStyleRowGroup.visible(dimParmActive.InventStyleIdFlag);        
    ProductImpactVersionRowGroup.visible(dimParmActive.InventVersionIdFlag);        
}

public CustVendTransportDays transportDays(DlvModeId _dlvMode, InventDim _inventDim)
{
    InventLocationId inventLocationId = _inventDim.InventLocationId ? _inventDim.InventLocationId : InventFallbackWarehouse::find(_inventDim.InventSiteId).InventLocationId;

    return CustVendTransportPointLine::defaultTransportDays(_dlvMode,
            inventLocationId,
            postalAddress.CountryRegionId,
            postalAddress.State,
            postalAddress.County,
            postalAddress.ZipCode);
}

private void populateATPInfo()
{
    SalesATPCalculation salesATPCalculation;

    if (SalesTmpDeliverySuggestions.SourcingOrigin == SalesSourcingOrigin::Intercompany)
    {
        ItemId          sourcingCompanyItemId = SalesLine.intercompanySourcingCompanyItemId(SalesTmpDeliverySuggestions.SourcingCompanyId);
        InventTransId   sourcingCompanyInventTransId = SalesLine.intercompanySalesLineInventTransId();
        InventDimId     sourcingCompanyInventDimId = SalesTmpDeliverySuggestions.inventDimInSourcingCompany().inventDimId;
        CustAccount     sourcingCompanyCustAccount = SalesLine.intercompanySalesLineCustomer().AccountNum;

        changecompany (SalesTmpDeliverySuggestions.SourcingCompanyId)
        {
            salesATPCalculation = SalesATPCalculation::newSalesCalculation(sourcingCompanyItemId, sourcingCompanyInventDimId, sourcingCompanyCustAccount);
            salesTmpATP.setTmpData(salesATPCalculation.calcSalesTmpATP(sourcingCompanyInventTransId, SalesLine.RemainInventPhysical));
        }
    }
    else
    {
        salesATPCalculation = SalesATPCalculation::newSalesCalculation(SalesLine.ItemId, SalesTmpDeliverySuggestions.InventDimId, SalesLine.CustAccount);
        salesTmpATP.setTmpData(salesATPCalculation.calcSalesTmpATP(SalesLine.InventTransId, SalesLine.RemainInventPhysical));
    }

    salesTmpATP_ds.executeQuery();

    AvailableToday_ATPQty.realValue(salesTmpATP.ATPQty);
    ATPTimeFence.value(salesATPCalculation.parmSalesATPSettingsMap().ATPTimeFence);
}

private void populateCTPInfo()
{
    if (!reqTransFormCTPExplosion)
    {
        reqTransFormCTPExplosion = ReqTransFormExplosion::newFormRun(element);
        reqTransFormCTPExplosion.tmpReqExplosionTree().DisplayReceiptDate = NoYes::Yes;
        reqTransFormCTPExplosion.tmpReqExplosionTree().DisplayReceiptCovQty = NoYes::Yes;
        reqTransFormCTPExplosion.tmpReqExplosionTree().DisplayItemId = NoYes::Yes;
        reqTransFormCTPExplosion.tmpReqExplosionTree().DisplayRefType = NoYes::Yes;
        reqTransFormCTPExplosion.parmCtrlTree(CTPTree);
        reqTransFormCTPExplosion.parmCtrlFormatExplain(CTPExplainControl);
        reqTransFormCTPExplosion.parmForceRecursiveBuild(true);
    }

    reqTransFormCTPExplosion.reconstruct(suggestionBuilder.detailedInfo(SalesTmpDeliverySuggestions.inventDim()));
    reqTransFormCTPExplosion.treeBuild();
}

private void populateAvailability()
{
    if (SalesTmpDeliverySuggestions.SourcingOrigin == SalesSourcingOrigin::Intercompany)
    {
        EcoResProductRecId productRecId = InventTable::itemProduct(SalesLine.ItemId);
        InventDim inventDimInSourcingCompany = SalesTmpDeliverySuggestions.inventDimInSourcingCompany();

        changecompany (SalesTmpDeliverySuggestions.SourcingCompanyId)
        {
            ItemId itemIdInSourcingCompany = InventTable::findByProduct(productRecId).ItemId;

            InventOnHandQty onHandQty = InventOnHandQty::newParameters(itemIdInSourcingCompany, inventDimInSourcingCompany);
            AvailableToday_PhysicalOnHandQty.realValue(onHandQty.physicalInvent());
            AvailableToday_ReservedPhysicalQty.realValue(onHandQty.reservPhysical());
            AvailableToday_AvailablePhysicalQty.realValue(onHandQty.availPhysical());

            AvailabilityUnitSymbol.text(InventTableModule::find(itemIdInSourcingCompany, ModuleInventPurchSales::Invent).UnitId);
        }
    }
    else
    {
        InventOnHandQty onHandQty = InventOnHandQty::newParameters(SalesLine.ItemId, SalesTmpDeliverySuggestions.inventDim());
        AvailableToday_PhysicalOnHandQty.realValue(onHandQty.physicalInvent());
        AvailableToday_ReservedPhysicalQty.realValue(onHandQty.reservPhysical());
        AvailableToday_AvailablePhysicalQty.realValue(onHandQty.availPhysical());

        AvailabilityUnitSymbol.text(InventTableModule::find(argsSalesLine.ItemId, ModuleInventPurchSales::Invent).UnitId);
    }
}

/// <summary>
/// Applies a special style (bold font) to the controls on the comparison tab where a value has changed.
/// </summary>
private void formatDeliveryAlternativeComparisonControls()
{
    for (int i = 1; i <= DeliveryAlternativeComparisonTab.controlCount(); i++)
    {
        FormGroupControl impactGroup = DeliveryAlternativeComparisonTab.controlNum(i) as FormGroupControl;

        if (impactGroup)
        {
            for (int j = 1; j <= impactGroup.controlCount(); j++)
            {
                FormGroupControl rowGroup = impactGroup.controlNum(j) as FormGroupControl;
                FormControl currentControl;
                FormControl selectedControl;

                for (int k = 1; k <= rowGroup.controlCount(); k++)
                {
                    FormControl rowControl = rowGroup.controlNum(k);

                    if (rowControl.dataSourceObject() == SalesLine_DS || rowControl.dataSourceObject() == InventDimSalesLine_DS)
                    {
                        currentControl = rowControl;
                    }
                    else if (rowControl.dataSourceObject() == SalesTmpDeliverySuggestions_DS || rowControl.dataSourceObject() == InventDimSuggestions_DS)
                    {
                        selectedControl = rowControl;
                    }
                }

                if (currentControl && selectedControl)
                {
                    this.setStyleForCurrentAndSelectedControlPair(currentControl, selectedControl);
                }
            }
        }
    }
}

private void setStyleForCurrentAndSelectedControlPair(FormControl _currentValueControl, FormControl _selectedValueControl)
{
    if (_currentValueControl.valueStr() != _selectedValueControl.valueStr())
    {
        _selectedValueControl.extendedStyle(this.extendedStyleModifiedField(_selectedValueControl));
    }
    else
    {
        _selectedValueControl.extendedStyle('');
    }
}

private str extendedStyleModifiedField(FormControl _formControl)
{
    if (_formControl is FormStringControl)
    {
        return enum2Symbol(enumNum(FormStringControlExtendedStyle), FormStringControlExtendedStyle::planning_fieldModified);
    }
    else if (_formControl is FormRealControl)
    {
        return enum2Symbol(enumNum(FormRealControlExtendedStyle), FormRealControlExtendedStyle::planning_fieldModified);
    }

    throw error(Error::wrongUseOfFunction(funcName()));
}

private void setStyleForRealFieldComparisonControls(FormRealControl _currentValueControl, FormRealControl _selectedValueControl)
{
    if (_currentValueControl.realValue() != _selectedValueControl.realValue())
    {
        _selectedValueControl.extendedStyle(enum2Symbol(enumNum(FormRealControlExtendedStyle), FormRealControlExtendedStyle::planning_fieldModified));
    }
    else
    {
        _selectedValueControl.extendedStyle('');
    }
}

