public int active()
{
    int ret;

    ret = super();

    boolean showAvailability = SalesTmpDeliverySuggestions.SourcingOrigin != SalesSourcingOrigin::ExternalVendor;

    AvailabilityInformationTab.visible(showAvailability);

    if (showAvailability)
    {
        element.populateAvailability();

        if (isATP)
        {
            element.populateATPInfo();
        }

        if (isCTP)
        {
            element.populateCTPInfo();
        }

        if (SalesLeadTime.visible())
        {
            SalesLeadTime.value(InventItemSalesSetup::find(SalesLine.ItemId, SalesTmpDeliverySuggestions.InventDimId).getLeadTime());
        }

        element.formatDeliveryAlternativeComparisonControls();
    }

    return ret;
}

