[Form]
public class SalesOrderArchiveForm extends FormRun
{
    private QueryBuildRange                     criteriaProjId;
    private InventDimCtrl_Frm_ActiveRightClick  inventDimFormSetup;

}

[Hookable(false)]
public Common docCursor()
{
    Common docCursor = super();

    if (docCursor.TableId == tableNum(InventDim))
    {
        docCursor = salesLineHistory;
    }

    return docCursor;
}

[Hookable(false)]
public void init()
{
    super();

    if ((element.args().callerName() == formstr(SalesOrderArchiveTransFormPart)
           && element.args().record() is ArchiveServiceJobDetailsView))
    {
        ArchiveServiceJobDetailsView archiveServiceJobDetailsView = element.args().record();

        salesTableHistory_ds.query()
                            .dataSourceTable(tableNum(SalesTableHistory))
                            .addRange(fieldNum(SalesTableHistory, ArchiveServiceJobId)).value(SysQuery::value(archiveServiceJobDetailsView.ArchiveServiceJobId));
    }

    element.updateDesign(InventDimFormDesignUpdate::Init);

    #SysSystemDefinedButtons
    FormCommandButtonControl attachButton= this.control(this.controlId(#SystemDefinedAttachButton)) as FormCommandButtonControl;
    attachButton.visible(false);
}

[Hookable(false)]
internal void updateDesign(InventDimFormDesignUpdate _mode)
{
    InventDimParm  inventDimParm;

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init :
            InventDimSetupGrid::initInventDimParmInventTrans(inventDimParm);

            if (!inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl_Frm_ActiveRightClick::newFromForm(element);
            }

            inventDimFormSetup.parmDimParmVisibleGrid(inventDimParm);
            inventDimFormSetup.formSetControls(true);
    }
}
