Method.Source:
    [Form]
    public class SalesPostingHistory extends FormRun
    {
        DocumentStatus      documentStatus;
        QueryRun            queryInForm;

        InventDimCtrl_Frm_Mov   inventDimFormSetup;
}

/// <summary>
///  Enables or disables <c>ButtonInventTrackingRegTransView</c> button on the form depending on the document type it is opened for and whether the item in the selected record has serial number dimension field activated in sales process
/// </summary>
public void enableLineButtons()
{
    if (ButtonInventTrackingRegTransView.isVisible())
    {
        ButtonInventTrackingRegTransView.enabled(salesParmLine && SalesParmLine.inventTable().isActiveInSalesProcess());
    }
}

void init()
{
    super();

    if (!element.args().parmEnumType())
    {
        throw error("@SYS22539");
    }

    documentStatus = element.args().parmEnum();
    if ((documentStatus != DocumentStatus::PackingSlip) && (documentStatus != DocumentStatus::Invoice))
    {
        ButtonInventTrackingRegTransView.visible(false);
    }
    this.design().caption(SysDictMenu::newMenuItem(element.args().menuItemName(), element.args().menuItemType()).label());

    queryInForm = new QueryRun(salesParmTable_ds.query());
    queryInForm.query().dataSourceTable(tablenum(SalesParmTable)).addRange(fieldnum(SalesParmTable, Ordering)).value(queryValue(documentStatus));
    salesParmTable_ds.query(queryInForm.query());

    element.updateDesign(InventDimFormDesignUpdate::Init);
}

void updateDesign(InventDimFormDesignUpdate mode)
{
    InventMovement  movement = InventMovement::construct(salesParmLine);

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init :
            if (!inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl_Frm_Mov::newFromFormMovement(element, movement);
            }

        case InventDimFormDesignUpdate::Active :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(), movement);
            inventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(), movement);
            inventDimFormSetup.formSetControls(false);
            break;

        default : throw error(strFmt("@SYS54195",funcName()));
    }
}

int active()
{
    int ret;

    ret = super();

    if (salesParmTable.ParmJobStatus == ParmJobStatus::ContainErrors)
    {
        buttonErrorLog.enabled(true);
    }
    else
    {
        buttonErrorLog.enabled(false);
    }

    return ret;
}

public int active()
{
    int ret;

    ret = super();

    element.enableLineButtons();
    element.updateDesign(InventDimFormDesignUpdate::Active);

    return ret;
}

void clicked()
{
    super();
    if (salesParmTable.Log)
        infolog.import(Info::infoStr2Con(salesParmTable.Log));
    else
        info("@SYS19186");
}

void clicked()
{
    Args    args;

    super();

    args = new Args();
    args.record(salesParmTable);
    args.parmEnum(enum2int(documentStatus));
    args.caller(element);

    SalesFormLetter::main(args);

    salesParmTable_ds.active();
}

public boolean modified()
{
    boolean ret;

    ret = super();

    SysQuery::findOrCreateRange(SalesParmTable_DS.queryBuildDataSource(), fieldnum(SalesParmTable, ParmJobStatus)).value(this.selection() == ParmJobStatusFilter::All ?
                                                                                                                    SysQuery::valueEmptyString() :
                                                                                                                    SysQuery::value(this.selection()));

    SalesParmTable_DS.executeQuery();

    return ret;
}
