[Form]
public class SalesQuotationCopying extends FormRun
{
    #resAppl

    TmpFrmVirtual          tmpFrmVirtualLines;
    TmpFrmVirtual          tmpFrmVirtualHeader;
    SalesQuotationTable    callerSalesQuotationTable;
    SalesPurchCopy         salesPurchCopy;
    ImageListAppl_Sales    imageListAppl_Sales;

    QueryBuildRange        typeRangeCustQuotationJour;
    QueryBuildRange        typeRangeCustQuotationConfirmJour;
    QueryBuildRange        typeRange;

    InventDimCtrl_Frm_Mov  inventDimFormSetup;

    boolean                allowCallerUpdate;

    boolean                 convertCurrency;
    TradeCurencyConversion  tradeCurencyConversion;

    [FormObservable]
    NoYes  copy;
}

void buildTreeControl()
{
    SalesQuotationCopyingTreeControlBuilder builder = SalesQuotationCopyingTreeControlBuilder::construct();
    builder.initialize(imageListAppl_Sales, custQuotationTrans, callerSalesQuotationTable);
    if (SalesQuotationCopyingDisplaySelectedLinesFlight::instance().isEnabled())
    {
        builder.parmTmpFrmVirtual(tmpFrmVirtualLines);
    }
    builder.build(treeControl);
}

boolean canClose()
{
    boolean         canClose;
    SalesQuotationCopyingContract salesQuotationCopyingContract;
    Object          salesQuotationTable_dataSource;
    Object          caller;

    canClose = super();

    try
    {
        if (element.closedOk() && canClose)
        {
            if (allowCallerUpdate)
            {
                if (copyHeader.value())
                {
                    select count(RecId) from tmpFrmVirtualHeader;
                    if (tmpFrmVirtualHeader.RecId > 1)
                    {
                        error("@SYS29009");
                        canClose = false;
                    }

                    [convertCurrency, tradeCurencyConversion] =
                        SalesQuotationCopying::promptConvertCurrencyCodeOnClient(
                            tmpFrmVirtualHeader, callerSalesQuotationTable, deleteLines.value(), copyMarkup.value()
                        );
                }
                else
                {
                    delete_from tmpFrmVirtualHeader;
                }

                if (canClose)
                {
                    salesQuotationCopyingContract = this.createContract();
                    SalesQuotationCopying::copyServer(salesQuotationCopyingContract);

                    salesQuotationTable_dataSource = FormDataUtil::getFormDataSource(callerSalesQuotationTable);
                    salesQuotationTable_dataSource.reRead();
                    salesQuotationTable_dataSource.reFresh();
                    if (formDataSourceHasMethod(salesQuotationTable_dataSource, identifierStr(editQuotation)))
                    {
                        salesQuotationTable_dataSource.editQuotation();
                    }

                    salesQuotationTable_dataSource.reReadReferenceDataSources();

                    if (formDataSourceHasMethod(salesQuotationTable_dataSource, identifierStr(reReadLines)))
                    {
                        salesQuotationTable_dataSource.reReadLines();
                    }

                    caller = element.args().caller();

                    if (formHasMethod(caller, identifierStr(refreshDimensionController)))
                    {
                        caller.refreshDimensionController();
                    }
                }
            }
        }
    }
    catch
    {
        canClose = false;
    }

    return canClose;
}

/// <summary>
/// Creates <c>SalesQuotationCopyingContract</c> object based on current UI values.
/// </summary>
/// <returns>
/// Instance of <c>SalesQuotationCopyingContract</c>.
/// </returns>
protected SalesQuotationCopyingContract createContract()
{
    SalesQuotationCopyingContract contract = SalesQuotationCopyingContract::construct();

    contract.parmSalesPurchCopy(salesPurchCopy);
    contract.parmCallingTableQuotationId(callerSalesQuotationTable.QuotationId);
    contract.parmTmpFrmVirtualLines(tmpFrmVirtualLines);
    contract.parmTmpFrmVirtualHeader(tmpFrmVirtualHeader);
    contract.parmQtyFactor(qtyFactor.realValue());
    contract.parmRecalculateAmount(recalculateAmount.value());
    contract.parmCopyMarkup(copyMarkup.value());
    contract.parmCopyPrecisely(copyPrecisely.value());
    contract.parmDeleteLines(deleteLines.value());
    contract.parmConvertCurrency(convertCurrency);
    contract.parmTradeCurencyConversion(tradeCurencyConversion);

    return contract;
}

NoYes editCopy(boolean          _set,
               Common           _line,
               Num              _id,
               LineNum          _lineNum,
               TransDate        _transDate,
               Qty              _qty,
               NoYes            _copy
               , boolean        _isCWItem = false,
               PdsCWInventQty   _cwQty = 0
               )
{
    element.findTmpFrmVirtual(tmpFrmVirtualLines, _line.TableId, _line.RecId);

    copy = tmpFrmVirtualLines ? NoYes::Yes : NoYes::No;

    if (_set)
    {
        if (_copy)
        {
            element.writeTmpFrmVirtual(tmpFrmVirtualLines,
                                       _line.TableId,
                                       _line.RecId,
                                       _id,
                                       _lineNum,
                                       _transDate,
                                       _qty
                                       , _isCWItem,
                                       _cwQty
                                       );

            copy   = NoYes::Yes;
        }
        else
        {
            tmpFrmVirtualLines.delete();
            copy   = NoYes::No;
        }
    }

    if (!SalesBuildTreeControlContext::current() || !SalesQuotationTreeControlNotBuildManyTimesFlight::instance().isEnabled())
    {
        element.buildTreeControl();
    }

    return copy;
}

/// <summary>
/// Gets or sets the quantity in catch weight unit.
/// </summary>
/// <param name="_set">
/// A Boolean value indicating if the quantity in catch weight unit should be set.
/// </param>
/// <param name="_line">
/// An originator table buffer.
/// </param>
/// <param name="_id">
/// An originator ID.
/// </param>
/// <param name="_lineNum">
/// An originator line number.
/// </param>
/// <param name="_transDate">
/// A transaction date.
/// </param>
/// <param name="_origCWQty">
/// An original quantity in catch weight unit.
/// </param>
/// <param name="_cwQty">
/// A quantity to be updated in catch weight unit.
/// </param>
/// <param name="_lineDataSource">
/// An originator form data source.
/// </param>
/// <param name="_itemId">
/// An Item ID.
/// </param>
/// <param name="_salesUnit">
/// A sales unit of measure.
/// </param>
/// <returns>
/// A quantity to be copied in catch weight unit.
/// </returns>
public PdsCWInventQty editCWQty(
    boolean         _set,
    Common          _line,
    Num             _id,
    LineNum         _lineNum,
    TransDate       _transDate,
    PdsCWInventQty  _origCWQty,
    PdsCWInventQty  _cwQty,
    FormDataSource  _lineDataSource,
    ItemId          _itemId,
    SalesUnit       _salesUnit)
{
    Object          lineDataSource = _lineDataSource;
    Qty             qty;
    boolean         isCWItem = PdsGlobal::pdsIsCWItem(_itemId);
    PdsCWInventQty  cwQty;

    element.findTmpFrmVirtual(tmpFrmVirtualLines, _line.TableId, _line.RecId);

    if (tmpFrmVirtualLines)
    {
        cwQty = tmpFrmVirtualLines.PdsCWQty;
    }
    else
    {
        cwQty = _origCWQty;
    }

    if (_set)
    {
        if (!tmpFrmVirtualLines)
        {
            lineDataSource.copy(true, _line, true);
        }

        cwQty   = _cwQty;
        qty     = PdsCatchWeight::inventQty(_itemId, cwQty, _salesUnit);

        element.writeTmpFrmVirtual(tmpFrmVirtualLines,
                                   _line.TableId,
                                   _line.RecId,
                                   _id,
                                   _lineNum,
                                   _transDate,
                                   qty,
                                   isCWItem,
                                   cwQty);
    }

    return cwQty;
}

NoYes editMarkAll(boolean           _set,
                  Common            _header,
                  Num               _id,
                  NoYes             _markAll,
                  FormDataSource    _lineDataSource)
{
    Object      lineDataSource = _lineDataSource;
    QueryRun    queryRun;
    NoYes       markAll;

    element.findTmpFrmVirtual(tmpFrmVirtualHeader, _header.TableId, _header.RecId);

    if (tmpFrmVirtualHeader)
    {
        markAll   = NoYes::Yes;
    }

    if (_set)
    {
        queryRun = new QueryRun(_lineDataSource.query());

        if (_markAll)
        {
            element.writeTmpFrmVirtual(tmpFrmVirtualHeader,
                                       _header.TableId,
                                       _header.RecId,
                                       _id);

            markAll= NoYes::Yes;

            if (SalesQuotationTreeControlNotBuildManyTimesFlight::instance().isEnabled())
            {
                using (SalesBuildTreeControlContext context = SalesBuildTreeControlContext::construct())
                {
                    while (queryRun.next())
                    {
                        lineDataSource.copy(true, queryRun.get(_lineDataSource.table()), true);
                    }
                }
                element.buildTreeControl();
            }
            else
            {
                while (queryRun.next())
                {
                    lineDataSource.copy(true, queryRun.get(_lineDataSource.table()), true);
                }
            }
        }
        else
        {
            tmpFrmVirtualHeader.delete();
            markAll= NoYes::No;

            while (queryRun.next())
            {
                lineDataSource.copy(true, queryRun.get(_lineDataSource.table()), false);
            }
        }

        lineDataSource.refreshEx(-1);
    }

    return markAll;
}

Qty editQty(boolean         _set,
            Common          _line,
            Num             _id,
            LineNum         _lineNum,
            TransDate       _transDate,
            Qty             _origQty,
            Qty             _qty,
            FormDataSource  _lineDataSource
            , ItemId          _itemId
            )
{
    Object  lineDataSource = _lineDataSource;
    Qty     qty;

    boolean isCWItem = PdsGlobal::pdsIsCWItem(_itemId);

    element.findTmpFrmVirtual(tmpFrmVirtualLines, _line.TableId, _line.RecId);

    if (tmpFrmVirtualLines)
    {
        qty = tmpFrmVirtualLines.Qty;
    }
    else
    {
        qty = _origQty;
    }

    if (_set
        && !isCWItem
        )
    {
        if (!tmpFrmVirtualLines)
        {
            lineDataSource.copy(true, _line, true);
        }

        qty = _qty;

        element.findTmpFrmVirtual(tmpFrmVirtualLines, _line.TableId, _line.RecId);

        element.writeTmpFrmVirtual(tmpFrmVirtualLines,
                                   _line.TableId,
                                   _line.RecId,
                                   _id,
                                   _lineNum,
                                   _transDate,
                                   qty);
    }

    return qty;
}

void findTmpFrmVirtual(TmpFrmVirtual _tmpFrmVirtual, tableId _tableId, recId _recId)
{
    select _tmpFrmVirtual
        where _tmpFrmVirtual.TableNum   == _tableId &&
              _tmpFrmVirtual.RecordNo   == _recId;
}

void  init()
{
    if (!element.args().record() || !element.args().parmEnumType())
    {
        throw error("@SYS22539");
    }

    callerSalesQuotationTable   = element.args().record();
    salesPurchCopy              = element.args().parmEnum();
    allowCallerUpdate           = hasTableAccess(element.args().record().TableId, AccessType::Add) && callerSalesQuotationTable.checkUpdate();

    super();

    element.showHideFields();

    imageListAppl_Sales = new ImageListAppl_Sales();

    treeControl.setImagelist(imageListAppl_Sales.imageList());

    qtyFactor.realValue(1);

    element.updateDesign(InventDimFormDesignUpdate::Init);

    switch (salesPurchCopy)
    {
        case SalesPurchCopy::CopyAllHeader:
            deleteLines.value(NoYes::Yes);
            copyHeader.value(NoYes::Yes);

        case SalesPurchCopy::CopyAllLines:
            element.design().caption("@SYS53043");
            break;
    }
    if (callerSalesQuotationTable.QuotationType == QuotationType::Project)
    {
        copyMarkup.visible(false);
        copyMarkup.value(false);
        recalculateAmount.visible(false);
        recalculateAmount.value(false);
    }
    else
    if (callerSalesQuotationTable.BusRelAccount && !callerSalesQuotationTable.CustAccount)
    {
        recalculateAmount.visible(false);
        recalculateAmount.value(false);
    }
}

Object inventDimSetupObject()
{
    return inventDimFormSetup;
}

