    rangeLineDeliveryType = this.query().dataSourceTable(tablenum(SalesQuotationLine)).addRange(fieldnum(SalesQuotationLine,LineDeliveryType));
    rangeLineDeliveryType.value(SysQuery::valueNot(LineDeliveryType::OrderLineWithMultipleDeliveries));
    rangeLineDeliveryType.status(RangeStatus::Hidden);

    this.cacheAddMethod(tablemethodStr(SalesQuotationLine,PdsCWUnitId));
    salesQuotationLine_ds.allowEdit(allowCallerUpdate);
}

edit SalesOrderedQty qty(boolean                _set,
                         SalesQuotationLine     _salesQuotationLine,
                         Qty                    _qty)
{
    Qty                 ret                     = _salesQuotationLine.SalesQty;
    Factor              salesQtyFactor          = 1.0;

    if (_salesQuotationLine.SalesQty)
    {
        salesQtyFactor = _qty / _salesQuotationLine.SalesQty;
    }

    ret = element.editQty(_set,
                          _salesQuotationLine,
                          _salesQuotationLine.QuotationId,
                          _salesQuotationLine.LineNum,
                          DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()),
                          _salesQuotationLine.SalesQty,
                          _qty,
                          salesQuotationLine_ds
                          , salesQuotationLine.ItemId
                          );

    return ret;
}

