    ret = element.editCopy(_set,
                           _salesQuotationLine,
                           _salesQuotationLine.QuotationId,
                           _salesQuotationLine.LineNum,
                           DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()),
                           _salesQuotationLine.SalesQty,
                           _copy
                           , cwItem,
                           cwQty
                           );
    return ret;
}

