/// <summary>
/// Allow user to edit catch weight information
/// </summary>
/// <param name="_set">
/// Specify whether the field should be updated
/// </param>
/// <param name="_custQuotationTrans">
/// Current CustQuotationTrans record
/// </param>
/// <param name="_cwQty">
/// Catch weight quantity to be updated
/// </param>
/// <returns>
/// Method returns the quantity that the was updated.
/// </returns>
/// <remarks>
/// If the user has a access to the field, the information is passed to a
/// form method where it is validated and if okay the virtual form information
/// is updated and value is returned.
/// </remarks>
edit PdsCWInventQty cwQty(
    boolean              _set,
    CustQuotationTrans   _custQuotationTrans,
    PdsCWInventQty       _cwQty)
{
    PdsCWInventQty       ret = _custQuotationTrans.PdsCWQty;

    if (hasFieldAccess(
        custQuotationTrans.TableId,
        fieldnum(CustQuotationTrans,PdsCWQty),
        AccessType::Edit))
    {
        ret = element.editCWQty(
            _set,
            _custQuotationTrans,
            _custQuotationTrans.QuotationId,
            _custQuotationTrans.LineNum,
            _custQuotationTrans.QuotationDate,
            _custQuotationTrans.PdsCWQty,
            _cwQty,
            custQuotationTrans_ds,
            custQuotationTrans.ItemId,
            custQuotationTrans.SalesUnit);
    }

    return ret;
}

void init()
{
    QueryBuildDataSource queryBuildDataSource;

