container checkParmTable(Num      _parmId,
                        boolean  custAccountInQuery,
                        boolean  invoiceAccountInQuery)
{
    Counter  numOfAccounts;
    Counter  numOfCurrency;
    SalesQuotationParmTable  salesQuotationParmT;

    numOfAccounts = 0;
    numOfCurrency = 0;

    if (custAccountInQuery)
    {
        while select salesQuotationParmT
                group by CustAccount
                where salesQuotationParmT.ParmId == parmId
        {
            numOfAccounts += 1;
        }
    }

    if (invoiceAccountInQuery)
    {
        while select salesQuotationParmT
                group by InvoiceAccount
                where salesQuotationParmT.ParmId == parmId
        {
            numOfAccounts += 1;
        }
    }

    while select salesQuotationParmT
            group by CurrencyCode
            where salesQuotationParmT.ParmId == parmId
    {
        numOfCurrency += 1;
    }

    return [numOfAccounts, numOfCurrency];
}

public void close()
{
    super();
    psaQuotationFormat_selection = psaQuotationFormat.selection();
    xSysLastValue::saveLast(this);
}

void closeOk()
{
    if (salesQuotationParmUpdate)
        salesQuotationParmUpdate_ds.write();

    if (isConfigurationkeyEnabled(configurationKeyNum(Project)))
    {
        salesQuotationEditLinesForm.initParameters(printFormletter.value(),
                                               transferHours2Forecast.value(),
                                               transferExpenses2Forecast.value(),
                                               transferFees2Forecast.value(),
                                               transferItems2Forecast.value(),
                                               reasonCode.text(),
                                               usePrintManagement.value(),
                                               salesQuotationParmUpdate,
                                               psaQuotationFormat.selection(),
                                               psaPrintNonCharge.value());
    }
    else
    {
        salesQuotationEditLinesForm.initParameters(printFormletter.value(),
                                               transferHours2Forecast.value(),
                                               transferExpenses2Forecast.value(),
                                               transferFees2Forecast.value(),
                                               transferItems2Forecast.value(),
                                               reasonCode.text(),
                                               usePrintManagement.value(),
                                               salesQuotationParmUpdate);
    }
    if (salesQuotationEditLinesForm.checkCloseDialog())
    {
        super();
    }
}

void  init()
{
    RunBase runBaseCaller;

    if (element.args() && element.args().caller() is RunBase)
    {
        runBaseCaller = element.args().caller();
    }

    if (!runBaseCaller || !runBaseCaller.runBase())
    {
        throw error("@SYS22539");
    }

    super();

    salesQuotationEditLinesForm = runBaseCaller.runBase();
    parmId                      = salesQuotationEditLinesForm.parmId();
    xSysLastValue::getLast(this);
    psaQuotationFormat.selection(psaQuotationFormat_selection);

    element.initParameters();

    switch (salesQuotationEditLinesForm.callerModuleAxapta())
    {
        case ModuleAxapta::Project                :
            Transfer2Forecast.visible(salesQuotationEditLinesForm.showProjTransferSelection());
            overviewGridProject.visible(true);
            overviewGrindLinesProj.visible(true);
            overviewGridSales.visible(false);
            overviewGrindLines.visible(false);
            salesQuotationParmTable_SalesIdManual.visible(false);
            if (isConfigurationkeyEnabled(configurationKeyNum(Project)))
            {
                printFormletter.value(false);
                psaQuotationFormat.allowEdit(false);

                if (salesQuotationEditLinesForm.documentStatus() == DocumentStatus::Confirmation ||
                    salesQuotationEditLinesForm.documentStatus() == DocumentStatus::Quotation)
                {
                    psaQuotationFormat.visible(true);
                    psaPrintNonCharge.visible(true);
                }
            }
            break;

        case ModuleAxapta::SalesOrder             :
            Transfer2Forecast.visible(false);
            overviewGridProject.visible(false);
            overviewGrindLinesProj.visible(false);
            overviewGridSales.visible(true);
            overviewGrindLines.visible(true);
            if ((salesQuotationEditLinesForm.documentStatus() == DocumentStatus::Confirmation) &&
                (SalesParameters::numRefSalesId().numberSequenceTable().Manual))
            {
                salesQuotationParmTable_SalesIdManual.visible(true);
                salesQuotationParmTable_SalesIdManual.enabled(true);
                salesQuotationParmTable_SalesIdManual.allowEdit(true);
            }

            break;
    }

    salesQuotationParmUpdate_ds.query().dataSourceTable(tableNum(SalesQuotationParmUpdate)).addRange(fieldNum(SalesQuotationParmUpdate, ParmId)).value(queryValue(parmId));
    salesQuotationParmTable_ds.query().dataSourceTable(tableNum(SalesQuotationParmTable)).addRange(fieldNum(SalesQuotationParmTable,ParmId)).value(queryValue(parmId));

    // <GTE>
    if (TaxSolutionScopeIntegrationUtil::isCompanyEnabled())
    {
        TaxDocumentLauncher.visible(true);
    }
    // </GTE>
}

void initParameters()
{
    printFormletter.value(salesQuotationEditLinesForm.printFormLetter());
    usePrintManagement.value(salesQuotationEditLinesForm.usePrintManagement());

    transferHours2Forecast.value   (salesQuotationEditLinesForm.transferHours2Forecast());
    transferExpenses2Forecast.value(salesQuotationEditLinesForm.transferExpenses2Forecast());
    transferFees2Forecast.value    (salesQuotationEditLinesForm.transferFees2Forecast());
    transferItems2Forecast.value   (salesQuotationEditLinesForm.transferItems2Forecast());

    if (salesQuotationEditLinesForm.reasonCode())
        reasonCode.text(salesQuotationEditLinesForm.reasonCode());
    else
        reasonCode.text("");
}

void initQuery()
{
    if (!salesQuotationEditLinesForm.queryChooseLines())
    {
        return;
    }

    Query query = salesQuotationEditLinesForm.queryChooseLines().query();

    if (query)
    {
        const int maxControls = 10;

        if (query.showInForm(LateSelectionRangeGroup, maxControls))
        {
            pleaseMakeSelection.visible(false);
        }
        else
        {
            pleaseMakeSelection.visible(true);
        }
    }
    tabPageLateSelection.visible(true);
}

void  run()
{
    if (parmId)
    {
        if (salesQuotationParmUpdate.LateSelection)
        {
            if (salesQuotationParmUpdate.LateSelectionQuery)
                salesQuotationEditLinesForm.queryChooseLines(new QueryRun(salesQuotationParmUpdate.LateSelectionQuery));

            element.initQuery();
        }
        salesQuotationEditLinesForm.setSalesQuotationDataSource(salesQuotationParmTable_ds);
    }

    super();

    printFormletter.label(salesQuotationEditLinesForm.formLetterCaption());

    reasonCodeGroup.visible(salesQuotationEditLinesForm.showReasonCode());

    transferLine.visible(salesQuotationEditLinesForm.showTransferLines());
    transferLine.allowEdit(salesQuotationEditLinesForm.allowEditTransferLines());
    element.setFieldAccess();
    this.design().caption(salesQuotationEditLinesForm.caption());
}

public int task(int _taskId)
{
    #task
    int ret;

    if (_taskId == #taskEsc)
    {
        _taskId = #taskCtrlQ;
    }

    ret = super(_taskId);
    return ret;
}

public boolean unpack(container packedClass)
{
    Integer     version     = conpeek(packedClass,1);

    switch (version)
    {
        case #CurrentVersion:
            [version,#CurrentList] = packedClass;
            break;
        default:
            return false;
    }
    return true;
}

public int active()
{
    int ret;
    boolean inventoried = salesQuotationParmLine.salesQuotationLine().isStocked();

    ret = super();

    salesQuotationParmLine_ds.object(fieldNum(SalesQuotationParmLine, InventNow)).allowEdit(inventoried);
    salesQuotationParmLine_ds.object(fieldNum(SalesQuotationParmLine, InventNow)).skip(!inventoried);
    salesQuotationParmLine_ds.object(fieldNum(SalesQuotationParmLine, RemainBeforeInvent)).allowEdit(inventoried);
    salesQuotationParmLine_ds.object(fieldNum(SalesQuotationParmLine, RemainBeforeInvent)).skip(!inventoried);
    salesQuotationParmLine_ds.object(fieldNum(SalesQuotationParmLine, RemainAfterInvent)).allowEdit(inventoried);
    salesQuotationParmLine_ds.object(fieldNum(SalesQuotationParmLine, RemainAfterInvent)).skip(!inventoried);

    return ret;
}

public void delete()
{
    QuotationId quotationId;
    ParmId  currentParmId;
    currentParmId  = salesQuotationParmLine.ParmId;
    quotationId = salesQuotationParmLine.OrigQuotationId;

    if (kittingFormHandler)
    {
        kittingFormHandler.checkResearch(this);
    }

    super();

    this.deleteSalesParmSubTable(quotationId,parmId);
    salesQuotationParmTable_ds.cacheCalculateMethod(tableMethodStr(SalesQuotationParmTable, checkIfLines));

    salesQuotationParmTable_ds.research();

    if (kittingFormHandler)
    {
        kittingFormHandler.DS_Delete(this);
    }
}

void deleteSalesParmSubTable(SalesId origQuotationId, ParmId _parmId)
{
    SalesQuotationParmLine       parmLine;
    SalesQuotationParmSubTable   subTable;

    select firstonly parmLine
        where parmLine.OrigQuotationId == origQuotationId &&
              parmLine.ParmId      == parmId;

    if ( !parmLine)
    {
        ttsbegin;
        delete_from subTable where
            subTable.ParmId == parmId &&
            subTable.OrigQuotationId == origQuotationId;
        ttscommit;
    }
}

public void deleteMarked()
{
    if (kittingFormHandler)
    {
        kittingFormHandler.startMultiSelectDeletion();
    }

    super();

    if (kittingFormHandler)
    {
        kittingFormHandler.endMultiSelectDeletion(this);
    }
}

void  clicked()
{
    super();

    salesQuotationEditLinesForm.salesQuotationParmUpdate(salesQuotationParmUpdate);

    if (!salesQuotationEditLinesForm.lateSelectionRemoveLines())
        return;

    salesQuotationEditLinesForm.showQueryForm(true);
    salesQuotationEditLinesForm.initLinesQuery();

    if (salesQuotationParmUpdate.LateSelection)
    {
        salesQuotationParmUpdate.LateSelectionQuery = salesQuotationEditLinesForm.queryChooseLines().query().pack();

        element.initQuery();
    }
    element.chooseTable();
}

boolean modified()
{
    boolean ret;

    ret = super();

    printFormletterChoice = printFormletter.value();

    return ret;
}

