public void calculateTotalDiscount()
{
    salesQuotationTable.updateFinalDisc();

    if (salesQuotationTable.Touched)
    {
        salesQuotationTable_ds.reread();
        salesQuotationTable_ds.refresh();
        salesQuotationTable_ds.active();
    }
}

void init()
{
    SalesQuotationMenuItemManager::newFormRun(this).run();

    super();

    if (TaxSolutionScopeIntegrationUtil::isCompanyEnabled())
    {
        TaxDocumentLauncher.visible(true);
        SalesTax.visible(false);
    }
}

/// <summary>
/// Customizes the options used to populate the Office Menu.
/// </summary>
/// <param name = "_menuOptions">The menu options to be customized.</param>
public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    ListIterator dataEntityOptionsIterator = new ListIterator(_menuOptions.dataEntityOptions());

    while (dataEntityOptionsIterator.more())
    {
        OfficeMenuDataEntityOptions dataEntityOptions = dataEntityOptionsIterator.value();

        if (dataEntityOptions.dataEntityName() == tableStr(ProjQuotationHeaderEntity) || dataEntityOptions.dataEntityName() == tableStr(ProjQuotationLineEntity))
        {
            dataEntityOptionsIterator.delete();
        }
        else
        {
            dataEntityOptionsIterator.next();
        }
    }
}

/// <summary>
/// Opens the related taskpage to edit a record.
/// </summary>
/// <remarks>
/// Different taskpages are opened depending on the type of the Quotation.
/// </remarks>
void clicked()
{
    Args args;

    super();

    args = new Args();
    if (salesQuotationTable.RecId != 0)
    {
        args.record(salesQuotationTable);
        args.caller(element);
        args.parmObject(element);
        args.openMode(OpenMode::Edit);
        args.copyCallerQuery(CopyCallerQuery::Yes);

        if (salesQuotationTable.QuotationType == QuotationType::Project)
        {
            new MenuFunction(menuitemDisplayStr(SalesQuotationProjTableForEdit), MenuItemType::Display).run(args);                  
        }
        else
        {
            new MenuFunction(menuitemDisplayStr(SalesQuotationTableForEdit), MenuItemType::Display).run(args);                 
        }
    }
}

public void clicked()
{
    if (CustParameters::find().AutomaticTotalDiscount)
    {
        element.calculateTotalDiscount();
    }

    super();
}

/// <summary>
/// Opens a form showing the taxes for the quotation.
