    SalesQuotationTotals::showTax(menuitemDisplayStr(TaxTmpWorkSalesQuote), salesQuotationTable);
}

void clicked()
{
    super();

    salesQuotationTable_ds.research(true);
    if (salesQuotationTable.updateMultiLineDisc())
    {
        // Calling write to ensure changes in TAE policies are persisted in the database.
        salesQuotationTable_ds.write();
        salesQuotationTable_ds.reread();
        salesQuotationTable_ds.refresh();
    }
}

public void  clicked()
{
    super();

    salesQuotationTable_ds.research(true);
    salesQuotationTable.updateFinalDisc();
    salesQuotationTable_ds.reread();
}

void clicked()
{
    if (RetailSalesQuotationCheckWorkflowRejectedLinesFlight::instance().isEnabled())
    {
        container callerRecordsPackedMap;

        if (salesQuotationTable_ds)
        {
            callerRecordsPackedMap = FormLetter::getFormRecord(salesQuotationTable_ds);
        }

        RetailPricingSalesDocumentHelper::validateQuotationsReadyForConfirm(callerRecordsPackedMap);
    }

    if (!SmmOpportunityStatusUpdate::updateFromQuote(salesQuotationTable_ds, smmOpportunityStatus::Won))
    {
        super();
    }
}

/// <summary>
/// Converst a business relation to a customer.
/// </summary>
/// <exception cref="Exception::Error">
/// Not allowed to store data in Customer table.
/// </exception>
void clicked()
{
    ProjQuotationId     activeQuotation;
    MenuFunction        menuFunction;
    NoYes               conversionSuccesfull;
    Args                args = new Args();

    if (smmBusRelTable.canConvertToCustomer()
    && (smmParameters::isNumberSequenceManual(extendedTypeNum(CustAccount))
    ||  Box::yesNo(strFmt("@SYS91001",salesQuotationTable.BusRelAccount),DialogButton::Yes,"@SYS81502")==DialogButton::Yes))
    {
        // Convert to customer
        conversionSuccesfull = smmBusRelTable::convert2Customer(smmBusRelTable::find(salesQuotationTable.BusRelAccount));

        if (conversionSuccesfull)
        {
            // Save active quotation
            activeQuotation = salesQuotationTable.QuotationId;

            SalesQuotationTable localSalesQuotationTable = SalesQuotationTable::find(activeQuotation);
            localSalesQuotationTable.mergeDimensionsAfterConversion();

            // Refresh the form
            salesQuotationTable_ds.reread();

            // Run through & refresh all business relations
            salesQuotationTable_ds.executeQuery();

            //  Find the original quotation you had active
            salesQuotationTable_ds.positionToRecordByValue(fieldNum(SalesQuotationTable, QuotationId), activeQuotation);

            // Open the Customer table form if configured in smmParameters to do so
            if (smmParameters::getBusRelOpenCustForm())
            {
                menuFunction = new MenuFunction(menuitemDisplayStr(CustTable), MenuItemType::Display);
                args.record(salesQuotationTable);
                menuFunction.run(args);
            }
        }
    }
}

/// <summary>
/// Refreshes the quotation after a change of reason code.
/// </summary>
void clicked()
{
    super();

    salesQuotationTable_ds.reread();
    salesQuotationTable_ds.refresh();
}

/// <summary>
/// Sets up the context for print management
/// </summary>
void clicked()
{
    PrintMgmtSetupContext setupContext = new PrintMgmtSetupContext();
    List                  salesDocTypes = new List(Types::Enum);

    if (salesQuotationTable_ds.cursor() == null)
    {
        return;
    }

    super();

    if (isConfigurationkeyEnabled(configurationKeyNum(Project)))
    {
        salesDocTypes.addEnd(PrintMgmtDocumentType::Confirmation);
        setupContext.addHierarchyContext(PrintMgmtHierarchyType::Sales, PrintMgmtNodeType::SalesQuotationTable, salesDocTypes);

        setupContext.addHierarchyContext(PrintMgmtHierarchyType::Project, PrintMgmtNodeType::ProjectQuotation);
    }
    else
    {
        setupContext.addHierarchyContext(PrintMgmtHierarchyType::Sales, PrintMgmtNodeType::SalesQuotationTable);
    }
    setupContext.parmCaller(element);
    setupContext.parmReferencedTableBuffer(salesQuotationTable_ds.cursor());
    setupContext.parmCountryRegionId(salesQuotationTable.custTable_CustAccount().postalAddress().CountryRegionId);
    PrintMgmt::launchSetup(setupContext);
}
