Method.Source:
    [Form]
    public class SalesQuotationPriceSimulation extends FormRun
    {
        boolean                             calledFromQuotationHeader;
        SalesQuotationLine                  salesQuotationLine;
        SalesQuotationTable                 salesQuotationTable;
        SalesQuotationPriceSimulationRecord salesQuotationPriceSimulationRecord;
}

void handleCaller()
{
    switch (element.args().dataset())
    {
        case(tablenum(SalesQuotationTable)):
            salesQuotationTable = element.args().record();
            salesQuotationPriceSimTable_ds.query().dataSourceTable(tablenum(SalesQuotationPriceSimTable)).addRange(fieldnum(SalesQuotationPriceSimTable, LineNum)).value(queryValue(0));
            salesQuotationPriceSimTable_ds.object(fieldnum(SalesQuotationPriceSimTable, ItemId)).visible(false);
            salesQuotationPriceSimTable_ds.object(fieldnum(SalesQuotationPriceSimTable, NewAmount)).visible(false);
            salesQuotationPriceSimTable_ds.object(fieldnum(SalesQuotationPriceSimTable, NewDiscountAmount)).visible(false);
            salesQuotationPriceSimTable_ds.object(fieldnum(SalesQuotationPriceSimTable, NewSalesPricePerUnit)).visible(false);
            simulatedCostPricePerUnit.visible(false);
            SimulatedPrice_NewAmount.visible(false);
            calledFromQuotationHeader = true;
            salesQuotationPriceSimulationRecord.setQuotationTable(salesQuotationTable);
            originalLinePrices.visible(false);
            simulatedResultDestination.visible(false);
            originalTotalPrices.visible(true);
            simulatedMultiLineDisc.visible(false);
            simulatedMultiLinePercentage.visible(false);
            simulatedSalesMarkUp.visible(false);
            simTotalDiscountPct.visible(true);
            break;

        case(tablenum(SalesQuotationLine)):
            calledFromQuotationHeader = false;
            salesQuotationLine = element.args().record();
            salesQuotationPriceSimulationRecord.setQuotationTable(SalesQuotationTable::find(salesQuotationLine.QuotationId));
            salesQuotationPriceSimulationRecord.setQuotationLine(salesQuotationLine);
            originalLinePrices.visible(true);
            simulatedResultDestination.visible(true);
            originalTotalPrices.visible(false);
            break;
    }

    if (salesQuotationPriceSimulationRecord.getSalesQuotationStatus() != SalesQuotationStatus::Created && salesQuotationPriceSimulationRecord.getSalesQuotationStatus() != SalesQuotationStatus::Sent)
    {
        applyPriceSimulation.enabled(false);
}
}

public void init()
{
    boolean allowEdit;
    super();

    salesQuotationPriceSimulationRecord = new SalesQuotationPriceSimulationRecord();

    if (!element.args().dataset())
    {
        // Sales quotation or Sales quotation line
        throw error(Error::missingFormActiveBuffer(element.name(), "@SYS94028"));
    }
    else
    {
        element.handleCaller();
    }

    if (calledFromQuotationHeader)
    {
        if (SalesQuotationPriceSimTable::existsSimLines(this.quotationId()))
        {
            // Simulation lines will be deleted if simulating on quotation header. Proceed?
            if (Box::yesNo("@SYS95351", DialogButton::No) == DialogButton::Yes)
            {
                SalesQuotationPriceSimTable::deleteSimulationLines(this.quotationId());
            }
            else
            {
                element.close();
            }
        }
    }
    else
    {
        if (SalesQuotationPriceSimTable::find(this.quotationId(), 0))
        {
            // Cannot simulate on lines when there are price simulations on quotation header
            error("@SYS95362");

            element.close();
        }
    }

    allowEdit = ((salesQuotationPriceSimulationRecord.getSalesQuotationStatus() == SalesQuotationStatus::Created || salesQuotationPriceSimulationRecord.getSalesQuotationStatus() == SalesQuotationStatus::Sent));

    salesQuotationPriceSimTable_ds.allowCreate(allowEdit);
    salesQuotationPriceSimTable_ds.allowDelete(allowEdit);
    salesQuotationPriceSimTable_ds.allowEdit(allowEdit);
    applyPriceSimulation.enabled(allowEdit && salesQuotationPriceSimTable.RecId ? true : false);
}

display AmountCur origTotalMargin()
{
    if (!salesQuotationLine || (salesQuotationLine && salesQuotationPriceSimTable.NewSalesPricePerUnit > 0))
    {
        return salesQuotationPriceSimulationRecord.getOrigTotalMargin();
    }

    return 0;
}

display AmountCur simMultiLineDisc()
{
    if (salesQuotationPriceSimTable.NewSalesPricePerUnit > 0)
    {
        return salesQuotationPriceSimulationRecord.getOrigMultiLineDisc();
    }

    return 0;
}

display Percent simMultiLinePercentage()
{
    if (salesQuotationPriceSimTable.NewSalesPricePerUnit > 0)
    {
        return salesQuotationPriceSimulationRecord.getOrigMultiLinePercentage();
    }

    return 0;
}

display AmountCur simSalesMarkUp()
{
    if (salesQuotationPriceSimTable.NewSalesPricePerUnit > 0)
    {
        return salesQuotationPriceSimulationRecord.getOrigSalesMarkUp();
    }

    return 0;
}

public void delete()
{
    super();

    applyPriceSimulation.enabled((salesQuotationPriceSimulationRecord.getSalesQuotationStatus() == SalesQuotationStatus::Created || salesQuotationPriceSimulationRecord.getSalesQuotationStatus() == SalesQuotationStatus::Sent) &&
                                  salesQuotationPriceSimTable.RecId ? true : false);
}

public int active()
{
    int ret;

    ret = super();

    if (ret)
    {
        element.calcPriceSim();

        applyPriceSimulation.enabled((salesQuotationPriceSimulationRecord.getSalesQuotationStatus() == SalesQuotationStatus::Created || salesQuotationPriceSimulationRecord.getSalesQuotationStatus() == SalesQuotationStatus::Sent) &&
                                      salesQuotationPriceSimTable.RecId ? true : false);
    }

    return ret;
}

public void initValue()
{
    super();

    if (calledFromQuotationHeader)
    {
        salesQuotationPriceSimTable.LineNum = 0;
        salesQuotationPriceSimTable.SimResult = SalesQuotationPriceSimResult::DiscountPercentage;
        element.calcPriceSim();
        salesQuotationPriceSimTable.NewDiscountPercentage   = element.origTotalDiscountPct();
        salesQuotationPriceSimTable.ContributionRatio       = element.origTotalRatio();
        salesQuotationPriceSimTable.Margin                  = element.origTotalMargin();
    }
    else
    {
        // Updating the line disc values does not make
        // sense if the AR disc parameter is set to only look
        // at multi line discounts when calculating the line
        // discount.  Therefore default the value to update the
        // line amount.
        if (salesparameters::find().Disc == LineMultiLn::MultiLn)
        {
            SalesQuotationPriceSimTable.SimResult = salesQuotationPriceSimResult::LineAmount;
        }
        salesQuotationPriceSimTable.ItemId                  = salesQuotationLine.ItemId;
        salesQuotationPriceSimTable.NewSalesPricePerUnit    = element.origSalesPricePerUnit();
        salesQuotationPriceSimTable.NewDiscountAmount       = element.origDiscountAmount();
        salesQuotationPriceSimTable.NewDiscountPercentage   = element.origDiscountPct();
        salesQuotationPriceSimTable.NewAmount               = element.origAmount();
        salesQuotationPriceSimTable.ContributionRatio       = element.origRatio();
        salesQuotationPriceSimTable.Margin                  = element.origMargin();
    }
}

public void modified()
{
    super();

    if (calledFromQuotationHeader)
    {
        salesQuotationPriceSimTable.Margin = salesQuotationPriceSimulationRecord.simMargin();
        salesQuotationPriceSimTable.NewDiscountPercentage = salesQuotationPriceSimulationRecord.discPercentageHeader();

        salesQuotationPriceSimTable_ds.write();
    }
    // Refresh the totals.
    else
    {
        element.calcPriceSim();
    }
}

public void modified()
{
    super();

    if (calledFromQuotationHeader)
    {
        salesQuotationPriceSimTable.NewDiscountPercentage = salesQuotationPriceSimulationRecord.discPercentageHeader();
        salesQuotationPriceSimTable.ContributionRatio = salesQuotationPriceSimulationRecord.ratioHeader();

        salesQuotationPriceSimTable_ds.write();
    }
    // Refresh the totals.
    else
    {
        element.calcPriceSim();
    }
}

/// <summary>
/// Calculates the sales unit price when the line amount changes.
/// </summary>
public void modified()
{
    SalesQuotationLine  salesQuotationLineLocal;

    super();

    // Load the discount values into a SalesQuotationLine buffer so we can call
    // discAmount() & discPercent() for the table buffer when calculating the new
    // unit price below.
    salesQuotationLineLocal.LineDisc        = salesQuotationPriceSimTable.NewDiscountAmount;
    salesQuotationLineLocal.MultiLnDisc     = salesQuotationLine.MultiLnDisc;
    salesQuotationLineLocal.LinePercent     = salesQuotationPriceSimTable.NewDiscountPercentage;
    salesQuotationLineLocal.MultiLnPercent  = salesQuotationLine.MultiLnPercent;

    // recalc the unit price - need the unit sales price to apply the price simulation correctly
    SalesQuotationPriceSimTable.NewSalesPricePerUnit =  PriceDisc::amount2Price(SalesQuotationPriceSimTable.NewAmount,
                                                            salesQuotationLineLocal.discPercent(),
                                                            SalesQuotationLine.SalesMarkup,
                                                            SalesQuotationLine.SalesQty,
                                                            salesQuotationLineLocal.discAmount(),
                                                            SalesQuotationLine.PriceUnit,
                                                            SalesQuotationLine.CurrencyCode,
                                                            SalesQuotationPriceSimTable.NewSalesPricePerUnit);
    // Refresh the totals.
    element.calcPriceSim();
}

public void modified()
{
    super();

    // Refresh the totals.
    salesQuotationPriceSimulationRecord.setPriceSimTable(salesQuotationPriceSimTable);
}

public void modified()
{
    super();

    if (calledFromQuotationHeader)
    {
        salesQuotationPriceSimTable.Margin = salesQuotationPriceSimulationRecord.marginHeader();
        salesQuotationPriceSimTable.ContributionRatio = salesQuotationPriceSimulationRecord.ratioHeader();

        salesQuotationPriceSimTable_ds.write();
    }
    // Refresh the totals.
    else
    {
        salesQuotationPriceSimulationRecord.setPriceSimTable(salesQuotationPriceSimTable);
    }
}

public void modified()
{
    super();

    // Refresh the totals.
    salesQuotationPriceSimulationRecord.setPriceSimTable(salesQuotationPriceSimTable);
}

void clicked()
{
    FormDataSource  callerDataSource;
    if (calledFromQuotationHeader)
    {
        callerDataSource = FormDataUtil::getFormDataSource(salesQuotationTable);

        salesQuotationPriceSimTable.updateQuotationHeader();

        // New prices and discounts are applied to the quotation header
        info("@SYS97950");
    }
    else
    {
        callerDataSource = FormDataUtil::getFormDataSource(salesQuotationLine);

        salesQuotationPriceSimTable.updateQuotationLine();

        // New prices and discounts are applied to the quotation line
        info("@SYS94247");
    }

    if (callerDataSource)
    {
        callerDataSource.reread();
        callerDataSource.refresh();
    }

    element.close();
}
