/// <summary>
/// Gets whether the system should create On-Account forecast entries.
/// </summary>
/// <param name = "_projTable">
/// The project record.
/// </param>
/// <returns>
/// true, if the system should create the On-Account forecast records; otherwise false.
/// </returns>
protected boolean shouldCreateOnAccountForecastV2(ProjTable _projTable)
{
    return this.shouldCreateOnAccountForecast(_projTable.Type) ||
            (enableProjMultipleContractLinesForProjectFeature &&
             _projTable.ProjInvoiceProjId &&
             ProjMultipleContractLinesForProjectFeatureHelper::existContractLineForBillingMethod(_projTable.ProjId, _projTable.ProjInvoiceProjId, ProjContractBillingMethod::FixedPrice));
}

public container pack()
{
    transferToProjForecast = TransferToProject.value();

    return [#CurrentVersion, #CurrentList];
}

public boolean unpack(container packedClass)
{
    Integer version = conpeek(packedClass, #CurrentVersion);

    switch (version)
    {
        case #CurrentVersion:
            [version, #CurrentList] = packedClass;
            break;
        default:
            return false;
    }
    return true;
}

void changeType()
{
    ProjTable       projTable;

    projTable.Type = newProjType.selection();

    if (!element.isProjTypeFixedPrice(projTable))
    {
        newEstimateProject.value(NoYes::No);
        controlId.text('');
        projPeriodId.text('');
        isFixedPriceProject = false;
    }
    else
    {
        newEstimateProject.value(NoYes::Yes);
        isFixedPriceProject = true;
    }

    projTableType  = projTable.type();

    projTableType.initProjTable();
    projTableType.createFormActive(element);

    projTableType.enableDisableFields();
}

boolean checkTransferItemsToItemReq()
{
    boolean retValue = true;

    // Test for items on quotation
    SalesQuotationLine      locsalesQuotationLine;
    select firstonly RecId from locsalesQuotationLine
        where locsalesQuotationLine.QuotationId        == sysWizard.parmSalesQuotationTable().QuotationId   &&
              locsalesQuotationLine.ProjTransType      == QuotationProjTransType::Item                      &&
              locsalesQuotationLine.Transferred2ItemReq      == NoYes::No;
    retValue = locsalesQuotationLine.RecId != 0;

    if (retValue)
    {
        // Is creation of Item requirements allow for project
        ProjTable projTableLocal;

        if (LinkToProjectSelection.selection() == enum2int(SalesQuotationLinkToProject::ExistingProject))
        {
            // validate based on current stage of project
            projTableLocal = ProjTable::find(ExistingProjId.text());
        }
        else
        {
            // Validate based on stage project will be created in
            projTableLocal.ProjId = CtrlProjId.text();
            projTableLocal.Type = newProjType.selection();
            projTableLocal.Status = ProjStatusTypeRule::firstStage(projTableLocal.Type);
            projTableLocal.CustAccount = CustAccount.text();
        }

        if (projTableLocal.ProjId != '')
        {
            //
            // Only validate if we have a project id.
            // Returning false based on incomplete form will cause the item requirements form
            // to be skipped.
            //
            ProjStatusType projStatusType = ProjStatusType::construct(ProjTableLocal);
            retValue = projStatusType.checkRule(ProjStatusRule::CreateItemTask);
        }
    }

    return retValue;
}

