/// <summary>
/// Inserts an item into the listView.
/// </summary>
/// <param name = "_description">A description of the item.</param>
/// <param name = "_countNew">The item's new amount of transactions.</param>
/// <param name = "_countExisting">The item's current amount of transactions.</param>
protected void listViewInsert(Description _description, int64 _countNew, int _countExisting)
{
    FormListItem fli = new FormListItem(_description, 1);
    fli.idx(1);

    int idx = listview.addItem(fli);
    listview.setText(idx, int642str(_countNew), 1);
    listview.setText(idx, int2str(_countExisting), 2);
}

/// <summary>
/// Add items to the ListView when not adding to the database.
/// </summary>
protected void addItemsToListView()
{
    stepsCount  = 0;

    listview.deleteAll();
    if (linkToProjectSelection.selection() == enum2int(SalesQuotationLinkToProject::NewProject))
    {
        this.listViewInsert("@SYS50498", 1, 0);
    }
    else
    {
        this.listViewInsert("@SYS50498", 0, 1);
    }

    if (element.transferToProject())
    {
        this.listViewInsert("@SYS84655", sysWizard.countForecastTransactions(QuotationProjTransType::Hour), 0);
        this.listViewInsert("@SYS84656", sysWizard.countForecastTransactions(QuotationProjTransType::Expense), 0);
        this.listViewInsert("@SYS84657", sysWizard.countForecastTransactions(QuotationProjTransType::Item), 0);
        this.listViewInsert("@SYS84658", sysWizard.countForecastTransactions(QuotationProjTransType::Fee), 0);
    }

    if (element.transferToProjectItemReq())
    {
        this.listViewInsert("@SYS84947", sysWizard.countForecastTransactions(QuotationProjTransType::Item, true), 0);
    }
}

