void endUpdate(boolean  _updateDB = false)
{
    if (!_updateDB)
    {
        this.addItemsToListView();
    }
    else
    {
        ttsbegin;

        ProjTable projTableLocal;
        boolean existQuotationABS = sysWizard.psaCheckExistABS();

        if (element.linkToNewProject())
        {
            this.createProject();
            sysWizard.linkQuotationToProject(element.newProjId(),
                                             element.newProjInvoiceProjId());
            sysWizard.linkConfirmationToProject(element.newProjId(),
                                             element.newProjInvoiceProjId());

            projTableLocal      = ProjTable::find(element.newProjId());
            isFixedPriceProject = projTableLocal.Type == element.getProjTypeForFixedPrice();
        }
        else
        {
            sysWizard.parmProjTable(ProjTable::find(existingProjId.text()));
            sysWizard.linkQuotationToProject(existingProjId.text(),
                                             existingProjInvoiceProjId.text());
            sysWizard.linkConfirmationToProject(existingProjId.text(),
                                             existingProjInvoiceProjId.text());

            projTableLocal = ProjTable::find(existingProjId.text());
            isFixedPriceProject = projTableLocal.Type == element.getProjTypeForFixedPrice();
        }

        if (existQuotationABS)
        {
            this.psaCheckABSTransferToProjectReleaseSoftCommitment();
        }

        if (this.shouldCreateOnAccountForecastV2(projTableLocal))
        {
            SalesQuotationTransferToProjectContract params = this.initializeSalesQuotationTransferParams(
                sysWizard.parmProjTable(projTableLocal), transferToProject.checked());
            SalesQuotationTransferToProject salesQuotationTransferToProject = new SalesQuotationTransferToProject();

            salesQuotationTransferToProject.parmSalesQuotationTransferToProjectContract(params);
            salesQuotationTransferToProject.parmSalesQuotationTable(sysWizard.parmSalesQuotationTable());
            salesQuotationTransferToProject.createForecastOnAcc(sysWizard.parmProjTable(projTableLocal), transferToProject.checked());
        }

        if (element.transferToProject())
        {
            this.setupTransferForecastToProject(existQuotationABS);

            sysWizard.transferForecastToProject();
        }

        if (element.transferToProjectItemReq())
        {
            if (existQuotationABS)
            {
                sysWizard.parmTransferActivityInfo(transferABSToProject.value());
            }
            sysWizard.transferItemReq();
        }

        sysWizard.updateTransferredToProject(element.transferToProject(), element.transferToProjectItemReq());

        ttscommit;
    }
}

protected SalesQuotationTransferToProjectContract initializeSalesQuotationTransferParams(ProjTable _projTable, boolean _transferToProjFlag)
{
    SalesQuotationTransferToProjectContract parameters = SalesQuotationTransferToProjectContract::constructSalesQuotationTransferToProjectContract(
        _projTable, sysWizard.parmSalesQuotationTable(), _transferToProjFlag);
    return parameters;
}

private void psaCheckABSTransferToProjectReleaseSoftCommitment()
{
    if (transferABSToProject.value())
    {
        sysWizard.psaTransferQuotationABSToProject(sysWizard.parmProjTable());
    }
    if (sysWizard.psaCheckExistSoftCommitment())
    {
        sysWizard.psaReleaseSoftCommitments();
    }
}

