void init()
{
    super();

    if (element.args().caller())
    {
        sysWizard = element.args().caller();
    }
    else
    {
        SalesQuotationProjLinkWizard::main(new Args());
        element.closeCancel();
    }

    if (!ProjTable::isNumberSeqManual())
    {
        ctrlProjId.allowEdit(false);
    }

    if (sysWizard.parmSalesQuotationTable())
    {
        transferABSToProject.visible(sysWizard.psaCheckExistABS());
        transferABSToProject.value(NoYes::No);

        element.design().caption(strFmt("@SYS85753", sysWizard.parmSalesQuotationTable().QuotationId));

        if (sysWizard.parmSalesQuotationTable().ProjIdRef == "")
        {
            linkToProjectSelection.selection(enum2int(SalesQuotationLinkToProject::NewProject));
        }
        existingProjId.text(sysWizard.parmSalesQuotationTable().ProjIdRef);
        existingProjInvoiceProjId.text(sysWizard.parmSalesQuotationTable().ProjInvoiceProjId);

        if (sysWizard.parmSalesQuotationTable().ModelId)
        {
            forcastModelId.text(sysWizard.parmSalesQuotationTable().ModelId);
        }
        else
        {
            forcastModelId.text(ProjParameters::find().ModelId);
        }

        custAccount.text(sysWizard.parmSalesQuotationTable().CustAccount);

        quotationIdCriteria.value(sysWizard.parmSalesQuotationTable().QuotationId);
        projTransTypeCriteria.value(enum2Value(QuotationProjTransType::Item));
        transferred2ItemReqCriteria.value(enum2Value(NoYes::No));

        quantityCriteria.value(strFmt('(((%1 > 0) && (%2 == %3)) || (%2 != %3))',
                               fieldStr(SalesQuotationLine, SalesQty),
                               fieldStr(SalesQuotationLine, ProjTransType),
                               enum2int(QuotationProjTransType::Item)));
        quotationIdForecastCriteria.value(sysWizard.parmSalesQuotationTable().QuotationId);

        // Restrive last saved values for SysLastValue.
        xSysLastValue::getLast(this);
        TransferToProject.value(transferToProjForecast);
        ForcastModelId.enabled(transferToProjForecast);
    }

    element.changeType();

    // <GIN>
    this.setVisible_IN();
    // </GIN>

    // Setup the last page of the wizard
    listview.addColumn(1,new FormListColumn("@SYS7576", 1, 130));
    listview.addColumn(2,new FormListColumn("@SYS55971", 2, 80));
    listview.addColumn(3,new FormListColumn("@SYS55972", 2, 80));
}

void initPage(int fromPage, int toPage)
{
    switch (fromPage)
    {
        case #FIRSTPAGE             :
            break;

        case #LINKTOPROJECT         :
            break;

        case #LINKTOEXISTINGPROJECT :
            break;

        case #LINKTONEWPROJECT      :
            break;

        case #TRANSFERTOFORECAST    :
            break;

        case #TRANSFERITEMREQ       :
            break;

        case #LASTPAGE              :
            CreatedCount.text(strFmt("@SYS84651",sysWizard.countTotal()));
            element.endUpdate();
            break;
    }

    switch (toPage)
    {
        case #FIRSTPAGE             :
            break;

        case #LINKTOPROJECT         :
            // When loading linkToProject page we disable linkToProjectSelection if
            // the quotation is already linked to project.
            if (sysWizard.parmSalesQuotationTable().ProjIdRef)
            {
                LinkToProjectSelection.allowEdit(false);
            }
            break;

        case #LINKTOEXISTINGPROJECT :
            // When loading linkToExistingProject we disable existingProjId field if
            // quotation is already linked to project
            if (sysWizard.parmSalesQuotationTable().ProjIdRef)
            {
                ExistingProjId.allowEdit(false);
            }
            break;

        case #LINKTONEWPROJECT      :
            // When loading linkToNewProject page we disable the calendar field if
            // quotation already has a calendar associated with it.
            SalesQuotationTable salesQuotationTable = sysWizard.parmSalesQuotationTable();
            if (salesQuotationTable.PSASchedCalendarId)
            {
                ProjTable_PSASchedCalendarId.text(salesQuotationTable.PSASchedCalendarId);
                ProjTable_PSASchedCalendarId.allowEdit(false);
            }
            break;

        case #TRANSFERTOFORECAST    :
            break;

        case #TRANSFERITEMREQ       :
            break;

        case #LASTPAGE    :
            element.endUpdate();
            break;
    }

    element.setNext();
}

