void setNext()
{
    ProjId  projId;

    ok = true;

    switch (sysWizard.curTab())
    {
        case #LINKTOPROJECT             :
            transferABSToProject.value(NoYes::Yes);
            break;

        case #LINKTOEXISTINGPROJECT     :
            ProjTable projTable = ProjTable::find(existingProjId.text());

            ok = (projTable &&
                  element.valid_QuotationProjType(projTable.Type) &&
                  (projTable.Status != ProjStatus::Completed));

            if (projTable &&
                transferABSToProject.value() &&
                !projTable.PSASchedCalendarId)
            {
                ok = ok && checkFailed(strFmt("@ProjPlan:ProjectCalendarRequiredToTransferWBS", projTable.ProjId));
            }

            if (!projTable.status().checkRule(ProjStatusRule::CreateQuotation))
            {
                ok = ok && checkFailed(strFmt("@SYS53010", projTable.Stage()));
            }

            projId = '';
            ctrlProjId.text(projId);
            break;

        case #LINKTONEWPROJECT          :
            ok = ((element.newProjId() || !ProjTable::isNumberSeqManual()) &&
                  (element.newProjInvoiceProjId() || element.enableProjMultipleContractLinesForProjectFeature) &&
                  (element.newProjGroupId() || element.enableProjMultipleContractLinesForProjectFeature) &&
                  element.valid_QuotationProjType(newProjType.selection()));

            SalesQuotationTable salesQuotationTable = sysWizard.parmSalesQuotationTable();

            // To display error while selecting new project on wizard for an on hold customers.
            CustVendorBlocked blockLevel = CustTable::blocked(salesQuotationTable.CustAccount);

            if (blockLevel == CustVendorBlocked::All || blockLevel == CustVendorBlocked::Invoice)
            {
                ok = checkFailed(strFmt("@SYS18389", salesQuotationTable.CustAccount, enum2str(blockLevel)));
            }

            if (ok)
            {
                if (salesQuotationTable
                    && transferABSToProject.value()
                    && !ProjTable_PSASchedCalendarId.valueStr())
                {
                    ok = ok && checkFailed("@ProjPlan:CalendarRequiredToTransferWBSToNewProject");
                }

                if (!name.text())
                {
                    name.text(sysWizard.parmSalesQuotationTable().QuotationName);
                }

                if (!ctrlProjId.text())
                {
                    projId = NumberSeq::newGetNum(ProjParameters::numRefProjId(),true).num();
                    ctrlProjId.text(projId);
                }
            }
            break;

        case #TRANSFERTOFORECAST        :
            if (this.valid_Project(ProjStatusRule::CreateForecast))
            {
                groupTransferToForecastWarning.visible(false);

                if ((sysWizard.countForecastTransactions(QuotationProjTransType::Hour) > 0) ||
                    (sysWizard.countForecastTransactions(QuotationProjTransType::Expense) > 0) ||
                    (sysWizard.countForecastTransactions(QuotationProjTransType::Item) > 0) ||
                    (sysWizard.countForecastTransactions(QuotationProjTransType::Fee) > 0))
                {
                    transferToProject.enabled(true);
                }
                else
                {
                    transferToProject.enabled(false);
                }
            }
            else
            {
                groupTransferToForecastWarning.visible(true);
                transferToProject.enabled(false);
            }
            if (transferToProject.value() == NoYes::Yes)
            {
                forecastModel = ForecastModel::find(HeadingSub::Heading,forcastModelId.text());
                if (!forecastModel)
                {
                    ok = false;
                }
                else if (forecastModel.Blocked)
                {
                    ok = checkFailed(strFmt("@SYS25943",forecastModel.ModelId));
                }
            }
            break;

        case #TRANSFERITEMREQ           :
            if (sysWizard.countForecastTransactions(QuotationProjTransType::Item, true) > 0)
            {
                transferToProjectItemReq.enabled(this.valid_Project(ProjStatusRule::CreateItemTask));
                groupTransferToItemReq.visible(!transferToProjectItemReq.enabled());
            }
            break;

        case #LASTPAGE              :
            CreatedCount.text(strFmt("@SYS84651",sysWizard.countTotal()));
            break;
    }

    sysWizard.nextEnabled(ok,sysWizard.curTab(),false);
}

