boolean valid_Project(ProjStatusRule _projStatusRule)
{
    boolean                 ret = true;
    ProjInvoiceProjId       projInvoiceProjId;
    ProjType                projType;
    ProjStatus              projStatus;
    ProjTable               projTable;
    str                     warningMsg;

    if (element.linkToNewProject())
    {
        projInvoiceProjId   = element.newProjInvoiceProjId();
        projType            = newProjType.selection();
        projStatus          = ProjStatusTypeRule::firstStage(projType);
    }
    else
    {
        projTable           = ProjTable::find(existingProjId.text());
        projInvoiceProjId   = projTable.ProjInvoiceProjId;
        projType            = projTable.Type;
        projStatus          = projTable.Status;
    }

    if (ret && !ProjStatusTypeRule::exist(projStatus, projType, _projStatusRule))
    {
        if (_projStatusRule == ProjStatusRule::CreateForecast)
        {
            ret = false;
            if (element.linkToNewProject())
            {
                warningMsg = "@SYS342109";
            }
            else
            {
                warningMsg = "@SYS342108";
            }
        }
    }

    if (!ret)
    {
        transferToForecastWarning.text(warningMsg);
        transferToItemReqWarning.text(warningMsg);
    }

    return ret;
}

boolean valid_QuotationProjType(ProjType _projType)
{
    return _projType == ProjType::TimeMaterial || _projType == ProjType::FixedPrice || _projType == ProjType::None;
}

