/// <summary>
///     Constructs a query for <c>ExistingProjId</c> lookup.
/// </summary>
/// <returns>
///     An instance of a query to construct <c>ExistingProjId</c> lookup.
/// </returns>
/// <remarks>
///      Extend this method to modify criteria on Project lookups.
/// </remarks>
protected Query buildProjIdQueryForLookp()
{
    QueryBuildDataSource    qbsProjTable, qbsProjInvoiceTable;
    Query                   query = new Query();
    CustAccount             quotationCustAccount;
    SalesQuotationTable     salesQuotationTable;
    QueryBuildDataSource    qbsBankLGGuaranteeProject;

    qbsProjTable = query.addDataSource(tableNum(ProjTable));
    qbsProjTable.addRange(fieldNum(ProjTable,Status)).value(strFmt('!%1',queryValue(ProjStatus::Completed)));

    if (!element.enableProjMultipleContractLinesForProjectFeature)
    {
        qbsProjTable.addRange(fieldNum(ProjTable,Type)).value(strFmt('%1,%2', queryValue(ProjType::TimeMaterial), queryValue(ProjType::FixedPrice)));
    }

    quotationCustAccount = sysWizard.parmSalesQuotationTable().CustAccount;

    if (CustTable::find(quotationCustAccount))
    {
        qbsProjTable.addRange(fieldNum(ProjTable,CustAccount)).value(quotationCustAccount);
    }

    // Add the conditions for tranfering LG from project quotation to project.
    salesQuotationTable = sysWizard.parmSalesQuotationTable();
    if (salesQuotationTable.isMarkedForBankLG())
    {
        qbsProjTable.addRange(fieldNum(ProjTable, BankDocumentType)).value(strFmt(
                '%1,%2',
                queryValue(BankLGDocumentType::None),
                queryValue(BankLGDocumentType::LetterOfGuarantee)));

        qbsBankLGGuaranteeProject = qbsProjTable.addDataSource(tableNum(BankLGGuaranteeProject));
        qbsBankLGGuaranteeProject.relations(true);
        qbsBankLGGuaranteeProject.joinMode(JoinMode::NoExistsJoin);
    }

    if (sysWizard.parmSalesQuotationTable().ProjInvoiceProjId)
    {
        qbsProjInvoiceTable = qbsProjTable.addDataSource(tableNum(ProjInvoiceTable));
        qbsProjInvoiceTable.relations(true);
        qbsProjInvoiceTable.addRange(fieldNum(ProjInvoiceTable,CurrencyId)).value(sysWizard.parmSalesQuotationTable().CurrencyCode);
        qbsProjInvoiceTable.addRange(fieldNum(ProjInvoiceTable, ProjInvoiceProjId)).value(sysWizard.parmSalesQuotationTable().ProjInvoiceProjId);
    }

    return query;
}

/// <summary>
///     Defines the default fields for Project lookups displayed.
/// </summary>
/// <param name = "_this">
///     An instance of <c>FormControl</c> class.
/// </param>
/// <returns>
///     An instance of <c>SysReferenceTableLookup</c> class which holds the fields.
/// </returns>
/// <remarks>
///     Extend this method to add or remove fields from Project lookups.
/// </remarks>

protected SysTableLookup addProjTableFieldsForLookup(FormControl _this)
{
    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(ProjTable), _this, true);

    sysTableLookup.addLookupfield(fieldNum(ProjTable, ProjId), true);
    sysTableLookup.addLookupfield(fieldNum(ProjTable, Name));
    sysTableLookup.addLookupfield(fieldNum(ProjTable, ProjInvoiceProjId));

    return sysTableLookup;

}

/// <summary>
///     Checks the project type for <c>ExistingProjId</c>.
/// </summary>
/// <param name = "_projTable">
///     An instance of <c>ProjTable</c>
/// </param>
/// <returns>
///     true if the project type is fixed price; otherwise, false.
/// </returns>
protected boolean isProjTypeFixedPrice(ProjTable _projTable)
{
    return _projTable.Type == ProjType::FixedPrice;
}

/// <summary>
///     Add project types to <c>LinkToNewProject</c> control.
/// </summary>
protected void addTypesToNewProjType()
{
    if (element.enableProjMultipleContractLinesForProjectFeature)
    {
        newProjType.selection(enum2int(projType::None));
        newProjType.visible(false);

        element.changeType();
    }
    else
    {
        newProjType.clear();
        newProjType.add(enum2str(ProjType::TimeMaterial));
        newProjType.add(enum2str(ProjType::FixedPrice));
    }
}

/// <summary>
///      Check if the project type is of type fixed price.
/// </summary>
/// <returns>
///     true if the project type is fixed price; otherwise, false.
/// </returns>
protected boolean checkProjTypeIsFixedPrice()
{
    return newProjType.selection() == ProjType::FixedPrice;
}

