}

display TransDate onAccTransDate()
{
    TransDate transDate = DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone());

    if (sysWizard.parmSalesQuotationTable().psaEstProjEndDate)
    {
        transDate = sysWizard.parmSalesQuotationTable().psaEstProjEndDate;
    }
    else if (sysWizard.parmSalesQuotationTable().psaEstProjStartDate)
    {
        transDate = sysWizard.parmSalesQuotationTable().psaEstProjStartDate;
    }

    return transDate;
}

